<form id="post_job" method="post" action=""><label>Công ty </label><?php echo $this->element->company; ?><label for="company" generated="false" class="error"><?php echo Core_Utils_Tools::printMessage($this->element->company);?></label><label>Cần tuyển <span class="require">*</span></label><?php echo $this->element->job_title; ?><label for="job_title" generated="false" class="error"><?php echo Core_Utils_Tools::printMessage($this->element->job_title);?></label><label>Làm việc tại <span class="require">*</span></label><?php echo $this->element->city; ?><label>Chi tiết công việc <span class="require">*</span></label><?php echo $this->element->job_description;?><label for="job_description" generated="false" class="error"><?php echo Core_Utils_Tools::printMessage($this->element->job_description);?></label><label>Email liên hệ <span class="require">*</span></label><?php echo $this->element->email_to;?><label for="email_to" generated="false" class="error"><?php echo Core_Utils_Tools::printMessage($this->element->email_to);?></label><label>Loại công việc </label><?php echo $this->element->job_type;?><label></label><button id="btSubmit" class="button">Đăng tuyển dụng</button></form><script>$(document).ready(function(){	$("#post_job").validate({	  rules: {		job_title: {			required: true		},		job_description: {			required: true		},		email_to: {			required: true,			email:true		}	  }	});})</script>