<form id="fRegistration" method="post" action=""><input type="hidden" name="isPopup" id="isPopup" value="0"/><table	style="width:100%">	<tr>		<td style="width:100px">Họ tên :</td>		<td><?php echo $this->element->name; ?> <span class="warning" title="Bắt buộc nhập">*</span>		<label for="name" generated="false" class="error"></label>		</td>	</tr>	<tr>		<td>Email :</td>		<td>			<?php echo $this->element->username; ?> <span class="warning" title="Bắt buộc nhập">*</span>			<label for="username" generated="false" class="error"></label>		</td>	</tr>	<tr>		<td>Mật khẩu :</td>		<td><?php echo $this->element->password; ?> <span class="warning" title="Bắt buộc nhập">*</span>		<label for="password" generated="false" class="error"></label>		</td>	</tr>	<tr>		<td>Số điện thoại :</td>		<td><?php echo $this->element->sodienthoai; ?></td>	</tr>	<tr>		<td></td>		<td>			<button id="btSubmit" class="button">Đăng ký</button>		</td>	</tr></table></form><script>$(document).ready(function(){	$("#fRegistration").validate({	  rules: {		password: {			required: true		},		name: {			required: true		},		username: {			required: true,			email:true		}	  }	});})</script>