<div style="width:99%"><?phpforeach($lstComment as $comment) {	if($comment['comment']['nhathau_id'] == null)		echo '<span style="font-weight:bold">'.$comment['comment']['ten'].'</span>';	else {		$link = BASE_PATH.'/nhathau/xem_ho_so/'.$comment['comment']['nhathau_id'].'/'.$comment['comment']['alias'];		echo '<span style="font-weight:bold"><a class="link" href="'.$link.'">'.$comment['comment']['ten'].'</a></span>';	}	?> : (<?php echo $html->getDaysFromSecond($comment["comment"]["ngaypost"]) ?> trước)<br/>	<div style="padding-left:10px"><?php echo $comment['comment']['ten']?></div>	<?php}?></div>