<?phpdefined('_JEXEC')or die('Restricted Access');require_once( JApplicationHelper::getPath('toolbar_html')); jimport('joomla.application.component.view');jimport('functionclass');class TourdulichViewTour extends JView {    function display($tpl=null) {                $items= &$this->get('Data');               $pagination = &$this->get('Pagination');        $filter = &$this->get('Filter');        $lists = &$this->get('Lists');        TOOLBAR_Danhmuc::_Danhmuc();        $this->assignRef('items',        $items);        $this->assignRef('pagination',    $pagination);        $this->assignRef('filter',        $filter);        $this->assignRef('lists',        $lists);        parent::display($tpl);    }    function edit($tpl=null) {                $info=&$this->get('Tour');        $isNew            =($info->id < 1);        $text            =$isNew ? TOOLBAR_Danhmuc::_NEW_Danhmuc():TOOLBAR_Danhmuc::_EDIT_Danhmuc() ;        $lstdiemden=array();        $lst1=array();        $lst2=array();        $lst3=array();        $i1=0;        $i2=0;        $model = & JModel::getInstance('Dmdiemden','TourdulichModel');        $modelloaitour = & JModel::getInstance('Loaitour','TourdulichModel');        $modelnhomtour = & JModel::getInstance('Nhomtour','TourdulichModel');        $modeldiemxuatphat = & JModel::getInstance('Dmdiemxuatphat','TourdulichModel');        $lstdiemden=$model->getData();        $lst1=$modelloaitour->getData();        $lst2=$modeldiemxuatphat->getData();        $lst3=$modelnhomtour->getData();        if($isNew==false)        {                                                $nhomtour=$modelnhomtour->getNhomtour($info->nhomtour);                        $diemxuatphat=$modeldiemxuatphat->getDiemxuatphat($nhomtour->diemxuatphat);                        $loaitour=$modelloaitour->getLoaitour($diemxuatphat->loaitour);                       $i1=$diemxuatphat->id;            $i2=$loaitour->id;        }        $this->assignRef('diemxuatphat',$i1);        $this->assignRef('loaitour',$i2);        $this->assignRef('info',$info);        $this->assignRef('lst1',$lst1);        $this->assignRef('lstdiemden',$lstdiemden);        $this->assignRef('lst2',$lst2);        $this->assignRef('lst3',$lst3);        parent::display($tpl);    }} ?>