/*Table structure for table `articles` */

DROP TABLE IF EXISTS `articles`;

CREATE TABLE `articles` (
  `id` bigint(20) NOT NULL AUTO_INCREMENT,
  `alias` varchar(255) DEFAULT NULL,
  `title` varchar(255) DEFAULT NULL,
  `imagedes` varchar(255) DEFAULT NULL,
  `contentdes` text,
  `content` text,
  `datemodified` datetime DEFAULT NULL,
  `usermodified` varchar(100) DEFAULT NULL,
  `viewcount` int(11) DEFAULT NULL,
  `active` tinyint(1) DEFAULT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=16 DEFAULT CHARSET=utf8;

/*Data for the table `articles` */

insert  into `articles`(`id`,`alias`,`title`,`imagedes`,`contentdes`,`content`,`datemodified`,`usermodified`,`viewcount`,`active`) values (2,'lo-dien-2-nu-sinh-danh-ban-bat-quy-thap-vincom','Lộ diện 2 nữ sinh đánh bạn, bắt quỳ  tháp Vincom','http://localhost/mycms/images/upload/1287644796_2[3].jpg','Một ngày sau khi clip nữ sinh đánh bạn, bắt quỳ được tung lên mạng, Công an quận Hai Bà Trưng (Hà Nội) đã xác định 3 nhân vật liên quan là học sinh THCS Vân Hồ và THCS Nguyễn Phong Sắc.','<p class=\"Lead\">Một ng&agrave;y sau khi clip nữ sinh đ&aacute;nh bạn, bắt quỳ được  tung l&ecirc;n mạng, C&ocirc;ng an quận Hai B&agrave; Trưng (H&agrave; Nội) đ&atilde; x&aacute;c định 3 nh&acirc;n vật  li&ecirc;n quan l&agrave; học sinh THCS V&acirc;n Hồ v&agrave; THCS Nguyễn Phong Sắc.<br />&gt; <a class=\"Lead\" href=\"http://vnexpress.net/GL/Xa-hoi/2010/10/3BA21810/\">X&ocirc;n xao clip nữ sinh đ&aacute;nh bạn, bắt quỳ</a></p>\n<p class=\"Normal\">Trao đổi với <em>VnExpress</em>, &ocirc;ng Nguyễn Như Thắng,  Trưởng ph&ograve;ng GD&amp;ĐT quận Hai B&agrave; Trưng cho biết, sau khi c&oacute; sự chỉ  đạo của Sở Gi&aacute;o dục, ph&ograve;ng đ&atilde; c&ugrave;ng trường THCS V&acirc;n Hồ phối hợp với c&ocirc;ng  an quận l&agrave;m r&otilde; vụ việc.</p>\n<p class=\"Normal\">Danh t&iacute;nh những nh&acirc;n vật trong clip đ&atilde; được x&aacute;c định.  Nạn nh&acirc;n l&agrave; học sinh lớp 8 THCS V&acirc;n Hồ, hai nữ sinh đ&aacute;nh bạn học lớp 8  v&agrave; lớp 9 THCS Nguyễn Phong Sắc. Quay clip l&agrave; hai nam sinh lớp 10 THPT  D&acirc;n lập Đinh Ti&ecirc;n Ho&agrave;ng. Địa điểm xảy ra vụ đ&aacute;nh nhau được x&aacute;c định l&agrave;  tại cầu thang bộ của to&agrave; nh&agrave; Vincom 2.</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\n<tbody>\n<tr>\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/18/B8/2%5B3%5D.jpg\" border=\"1\" alt=\"\" width=\"480\" height=\"350\" /></td>\n</tr>\n<tr>\n<td class=\"Image\">Nữ sinh đồng &yacute; giải quyết m&acirc;u thuẫn bằng nắm đấm. <em>Ảnh chụp từ clip.</em></td>\n</tr>\n</tbody>\n</table>\n<p class=\"Normal\">Theo lời khai của những người li&ecirc;n quan, chiều 30/9  hai nữ sinh trường Nguyễn Phong Sắc đi học về, đến Trung t&acirc;m thương mại  Vincom th&igrave; gặp nữ sinh trường THCS V&acirc;n Hồ (trường Nguyễn Phong Sắc đang  học nhờ cơ sở của trường V&acirc;n Hồ). Do m&acirc;u thuẫn trước đ&oacute; n&ecirc;n c&aacute;c nữ sinh  đ&atilde; dẫn nhau đến cầu thang bộ trung t&acirc;m thương mại Vincom 2 để giải  quyết.</p>\n<p class=\"Normal\">Hai học sinh lớp 10 l&agrave; bạn của hai nữ sinh trường  Nguyễn Phong Sắc biết chuyện, đ&atilde; rủ nhau v&agrave;o xem, d&ugrave;ng điện thoại để  quay lại to&agrave;n bộ sự việc. Ng&agrave;y 10/10, ch&iacute;nh cậu học tr&ograve; tr&ecirc;n đ&atilde; nhờ bạn  tung clip l&ecirc;n mạng.</p>\n<p class=\"Normal\">Clip xuất hiện tr&ecirc;n trang Youtube ng&agrave;y 10/10 với thời  lượng hơn 3 ph&uacute;t đ&atilde; g&acirc;y x&ocirc;n xao dư luận. Ba nh&acirc;n vật ch&iacute;nh trong clip  đều mặc đồng phục học sinh, trong đ&oacute; nạn nh&acirc;n li&ecirc;n tục bị hai nữ sinh  k&eacute;o t&oacute;c, t&aacute;t, đấm, đ&aacute; v&agrave;o mặt. Th&ocirc; bạo hơn, họ c&ograve;n bắt c&ocirc; g&aacute;i quỳ, ngẩng  mặt l&ecirc;n để đ&aacute;nh v&agrave; chỉ bỏ đi sau khi đ&atilde; lột &aacute;o.</p>\n<p class=\"Normal\" style=\"text-align: right;\"><strong>Ho&agrave;ng Th&ugrave;y</strong></p>','2010-10-21 15:12:10','ADMIN',8,1),(3,'han-quoc-nghi-trieu-tien-sap-thu-hat-nhan','Hàn Quốc nghi Triều Tiên sắp thử hạt nhân','http://localhost/mycms/images/upload/1287645605_b2.jpg','Triều Tiên đang tập trung người và thiết bị tại điểm thử hạt nhân hai lần trước và có thể chuẩn bị cho lần thử thứ ba, báo chí Hàn Quốc cho biết.','<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"left\">\n<tbody>\n<tr>\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/1E/50/b1.jpg\" alt=\"Cơ sở hạt nh&acirc;n của Triều Ti&ecirc;n. Ảnh: EPA.\" width=\"250\" height=\"195\" /></td>\n</tr>\n<tr>\n<td class=\"Image\">Cơ sở hạt nh&acirc;n Yongbyon của Triều Ti&ecirc;n. Ảnh: <em>EPA.</em></td>\n</tr>\n</tbody>\n</table>\n<p class=\"Lead\">Triều Ti&ecirc;n đang tập trung người v&agrave; thiết bị tại điểm thử  hạt nh&acirc;n hai lần trước v&agrave; c&oacute; thể chuẩn bị cho lần thử thứ ba, b&aacute;o ch&iacute;  H&agrave;n Quốc cho biết.</p>\n<p class=\"Normal\">Tờ <em>Chosun Ilbo</em> của H&agrave;n Quốc n&oacute;i rằng ảnh chụp  qua vệ tinh Mỹ cho thấy hoạt động của người v&agrave; phương tiện tại b&atilde;i thử  hạt nh&acirc;n của Triều Ti&ecirc;n tăng l&ecirc;n. \"Hoạt động di chuyển của người v&agrave;  phương tiện tại Punggye-ri diễn ra rầm rộ gần đ&acirc;y\", tờ b&aacute;o n&agrave;y dẫn nguồn  tin ch&iacute;nh phủ giấu t&ecirc;n cho hay.</p>\n<p class=\"Normal\">B&igrave;nh Nhưỡng c&oacute; vẻ cũng đang kh&ocirc;i phục c&aacute;c đường hầm m&agrave;  họ ph&aacute; hủy trong hai vụ thử hạt nh&acirc;n lần trước. \"Tuy vậy, kh&ocirc;ng c&oacute; vẻ  l&agrave; Triều Ti&ecirc;n sẽ sớm tiến h&agrave;nh vụ thử. C&oacute; thể việc chuẩn bị sẽ mất  khoảng 3 th&aacute;ng\", nguồn tin n&agrave;y cho biết th&ecirc;m.</p>\n<p class=\"Normal\">Trong khi đ&oacute;, một quan chức Bộ Quốc ph&ograve;ng H&agrave;n Quốc n&oacute;i  rằng đ&oacute; c&oacute; thể chỉ l&agrave; hoạt động bảo dưỡng c&aacute;c thiết bị chiến lược định  kỳ.</p>\n<p class=\"Normal\">Triều Ti&ecirc;n thử hạt nh&acirc;n lần đầu ti&ecirc;n hồi th&aacute;ng 10/2006  v&agrave; lần thứ hai v&agrave;o th&aacute;ng 5/2009 tại Punggye-ri, tỉnh Bắc Hamgyong.  Th&ocirc;ng tin tr&ecirc;n được tờ <em>Chosun </em>đưa ra giữa l&uacute;c H&agrave;n Quốc chuẩn bị cho hội nghị thượng đỉnh G20.</p>\n<p class=\"Normal\">Theo <em>AFP</em>, B&igrave;nh Nhưỡng tuy&ecirc;n bố h&ocirc;m 16/10 rằng  họ sẵn s&agrave;ng nối lại đ&agrave;m ph&aacute;n 6 b&ecirc;n về giải gi&aacute;p hạt nh&acirc;n nhưng sẽ kh&ocirc;ng  vội v&atilde; v&igrave; Mỹ v&agrave; một số nước kh&aacute;c chưa sẵn s&agrave;ng.</p>\n<p class=\"Normal\">Washington n&oacute;i rằng B&igrave;nh Nhưỡng phải cải thiện quan hệ với Seoul v&agrave; b&agrave;y tỏ sự ch&acirc;n th&agrave;nh trước khi nối lại đ&agrave;m ph&aacute;n.</p>\n<p class=\"Normal\">C&aacute;c cuộc đ&agrave;m ph&aacute;n s&aacute;u b&ecirc;n c&oacute; sự tham gia của Triều  Ti&ecirc;n, H&agrave;n Quốc, Trung Quốc, Nhật Bản, Nga v&agrave; Mỹ suốt mấy năm qua đ&atilde; l&acirc;m  v&agrave;o bế tắc. B&igrave;nh Nhưỡng r&uacute;t lui hồi th&aacute;ng 4/2009 để phản đối việc Li&ecirc;n  Hợp Quốc tăng cường cấm vận sau khi họ thử hạt nh&acirc;n v&agrave; t&ecirc;n lửa.</p>\n<p class=\"Normal\" style=\"text-align: right;\"><strong>Ngọc Sơn</strong></p>','2010-10-21 15:21:14','ADMIN',10,1),(4,'vinashin-&-pha-san-theo-kieu-viet-nam','Vinashin & phá sản\' theo kiểu Việt Nam','http://localhost/mycms/images/upload/1287645742_2a.jpg','Trao đổi với báo chí sáng nay, Ủy viên Ủy ban Kinh tế Quốc hội Nguyễn Đức Kiên cho rằng về mặt khoa học Nhà nước đã cho Vinashin phá sản, có chăng chỉ chưa tuyên bố chính thức.','<p class=\"Lead\">Trao đổi với b&aacute;o ch&iacute; s&aacute;ng nay, Ủy vi&ecirc;n Ủy ban Kinh tế  Quốc hội Nguyễn Đức Ki&ecirc;n cho rằng về mặt khoa học Nh&agrave; nước đ&atilde; cho  Vinashin ph&aacute; sản, c&oacute; chăng chỉ chưa tuy&ecirc;n bố ch&iacute;nh thức.<br />&gt; <a class=\"Lead\" href=\"http://vnexpress.net/GL/Kinh-doanh/2010/10/3BA21E0D/\">\'Kh&ocirc;ng cơ quan n&agrave;o chịu tr&aacute;ch nhiệm ch&iacute;nh về Vinashin\'</a></p>\n<p class=\"Normal\">Những rối ren tại Tập đo&agrave;n C&ocirc;ng nghiệp T&agrave;u thủy Việt  Nam (Vinashin) đang l&agrave; chủ đề sau khi Ch&iacute;nh phủ gửi b&aacute;o c&aacute;o chi tiết tới  c&aacute;c đại biểu Quốc hội chiều qua.</p>\n<p class=\"Normal\"><em><span style=\"font-family: Calibri;\">- V</span>ới khoản nợ l&ecirc;n tới hơn 85.000 tỷ đồng v&agrave; kh&ocirc;ng c&oacute; khả năng chi trả, theo &ocirc;ng, tại sao kh&ocirc;ng để Vinashin tuy&ecirc;n bố ph&aacute; sản?</em></p>\n<p class=\"Normal\">- Đứng về mặt khoa học kinh tế th&igrave; Nh&agrave; nước đ&atilde; cho  Vinashin ph&aacute; sản rồi, c&oacute; điều kh&ocirc;ng tuy&ecirc;n bố th&ocirc;i. Việc chuyển đổi một  số ng&agrave;nh nghề kinh doanh sang doanh nghiệp kh&aacute;c, cơ cấu lại, chuyển đổi  m&ocirc; h&igrave;nh quản l&yacute;, t&iacute;nh to&aacute;n lại nợ&hellip; l&agrave; những biểu hiện.</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\n<tbody>\n<tr>\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/1E/78/Photo0073.jpg\" border=\"1\" alt=\"Vinashin đang được cho ph&aacute; sản theo kiểu đặc th&ugrave; của Việt Nam. Ảnh: K.L\" width=\"480\" height=\"320\" /></td>\n</tr>\n<tr>\n<td class=\"Image\">Vinashin đang được cho ph&aacute; sản theo kiểu đặc th&ugrave; của Việt Nam. Ảnh: K.L</td>\n</tr>\n</tbody>\n</table>\n<p class=\"Normal\">Tuy nhi&ecirc;n, Vinashin l&agrave; một tập đo&agrave;n nh&agrave; nước m&agrave; Ch&iacute;nh  phủ l&agrave; chủ sở hữu. N&ecirc;n c&aacute;ch ứng xử phải kh&aacute;c. Tập đo&agrave;n ấy c&ograve;n li&ecirc;n quan  đến 7 vạn lao động đang l&agrave;m việc tại đ&acirc;y. Th&ecirc;m v&agrave;o đ&oacute;, phải đặt việc ph&aacute;  sản trong bối cảnh của năm 2010 khi m&agrave; quỹ bảo hiểm thất nghiệp mới  h&igrave;nh th&agrave;nh năm 2009 v&agrave; hiện giờ th&igrave; vẫn chưa đủ thời gian để h&igrave;nh th&agrave;nh  nguồn để chi trả.</p>\n<p class=\"Normal\">Khi tuy&ecirc;n bố ph&aacute; sản th&igrave; doanh nghiệp kh&ocirc;ng chịu tr&aacute;ch  nhiệm về những khoản lỗ, cũng như c&aacute;c vấn đề g&igrave; nảy sinh tiếp theo với  doanh nghiệp nhưng với Vinashin th&igrave; kh&ocirc;ng thể l&agrave;m thế. Với c&aacute;ch ph&aacute; sản  đặc th&ugrave; của Vinashin, người lao động kh&ocirc;ng bị đẩy ra đường, c&aacute;c khoản nợ  ng&acirc;n h&agrave;ng vẫn được đảm bảo&hellip; v&agrave; Ch&iacute;nh phủ vẫn đảm bảo được sự ổn định vĩ  m&ocirc;.</p>\n<p class=\"Normal\"><em>- Trong qu&aacute; tr&igrave;nh t&aacute;i cơ cấu, Vinashin chuyển một  số khoản nợ sang Petrovietnam v&agrave; Vinalines khiến nhiều người lo ngại đ&acirc;y  l&agrave; h&igrave;nh thức chuyển nợ cho doanh nghiệp nh&agrave; nước kh&aacute;c trả thay Ch&iacute;nh  phủ. &Ocirc;ng nghĩ sao?</em></p>\n<p class=\"Normal\">- Cũng kh&ocirc;ng hẳn thế. Việc chuyển nợ k&egrave;m theo nhiều  việc kh&aacute;c l&agrave; h&igrave;nh thức để chuy&ecirc;n m&ocirc;n h&oacute;a cho Vinashin. Trước đ&acirc;y, mọi  người cứ ph&ecirc; ph&aacute;n Vinashin kinh doanh đa ng&agrave;nh, rời xa ng&agrave;nh ch&iacute;nh th&igrave;  b&acirc;y giờ t&aacute;ch ra chỉ tập trung v&agrave;o chuy&ecirc;n m&ocirc;n của họ th&ocirc;i. C&aacute;c ng&agrave;nh kh&aacute;c  th&igrave; chuyển sang doanh nghiệp c&oacute; lợi thế hơn trong kinh doanh.</p>\n<p class=\"Normal\"><em>- Nhưng khi chuyển như vậy th&igrave; bản th&acirc;n c&aacute;c khoản nợ cũng chưa r&otilde; r&agrave;ng, tại sao kh&ocirc;ng kiểm to&aacute;n trước rồi mới chuyển?</em></p>\n<p class=\"Normal\">- Giống như nh&agrave; c&oacute; 3 người con, một người bị bệnh  thận, &ocirc;ng bố bảo 2 người c&ograve;n lại g&oacute;p tiền để đưa đi thay thận. L&uacute;c đ&oacute;  th&igrave; kh&ocirc;ng n&ecirc;n hỏi kiểu: &ldquo;Nếu con bỏ ra 10 triệu đồng nhỡ em kh&ocirc;ng trả  được th&igrave; sao?&rdquo;. C&ugrave;ng một chủ sở hữu cả n&ecirc;n cũng kh&ocirc;ng cần thiết phải l&agrave;m  việc đ&oacute;.</p>\n<p class=\"Normal\"><em>- Trong vụ việc tại Vinashin, b&aacute;o c&aacute;o gi&aacute;m s&aacute;t của  Quốc hội đ&atilde; c&oacute; cảnh b&aacute;o nhưng rồi những sự việc sai phạm vẫn tiếp diễn.  Tr&aacute;ch nhiệm của Quốc hội đến đ&acirc;u?</em></p>\n<p class=\"Normal\">- Trong b&aacute;o c&aacute;o gi&aacute;m s&aacute;t về quản l&yacute; vốn của tập đo&agrave;n  năm 2009, tổ gi&aacute;m s&aacute;t c&oacute; n&oacute;i r&otilde; l&agrave; phải tiến h&agrave;nh cơ cấu lại v&agrave; ban h&agrave;nh  luật quản l&yacute; vốn v&agrave; t&agrave;i sản nh&agrave; nước. Tuy nhi&ecirc;n, khi biểu quyết th&igrave;  Quốc hội kh&ocirc;ng th&ocirc;ng qua. Ở đ&acirc;y, Quốc hội cũng c&oacute; lỗi.</p>\n<p class=\"Normal\"><em>- Như vậy th&igrave; tr&aacute;ch nhiệm của tổ gi&aacute;m s&aacute;t ra sao?</em></p>\n<p class=\"Normal\">- Việc đưa ra vấn đề đ&uacute;ng nhưng kh&ocirc;ng thuyết phục được  Quốc hội th&igrave; phải xem x&eacute;t lại phương ph&aacute;p thuyết phục của người được  giao nhiệm vụ đ&oacute;. Phương ph&aacute;p thuyết phục c&oacute; thể chưa đ&uacute;ng. Một v&iacute; dụ  hiển nhi&ecirc;n l&agrave; thấy đ&egrave;n đỏ phải dừng lại theo luật để đảm bảo an to&agrave;n cho  m&igrave;nh v&agrave; nhiều người kh&aacute;c, nhưng nhiều người vẫn vượt qua đ&egrave;n đỏ.</p>\n<p class=\"Normal\" style=\"text-align: right;\"><strong>Ho&agrave;ng Ly</strong></p>','2010-10-23 22:32:53','admin',63,1),(5,'victor-vu-giao-lo-dinh-menh-giong-shattered-den-bat-ngo','Victor Vũ: \"Giao lộ định mệnh\' giống \"Shattered` đến bất ngờ','http://localhost/mycms/images/upload/1287645821_sub.jpg','Đạo diễn Việt kiều cho biết, anh rùng mình khi xem xong \'Shattered\' của đạo diễn Hollywood Wolfgang Petersen thực hiện năm 1991. Tác phẩm này và \"Giao lộ định mệnh\" của anh có quá nhiều điểm giống nhau.','<p class=\"Lead\">Đạo diễn Việt kiều cho biết, anh r&ugrave;ng m&igrave;nh khi xem xong  \'Shattered\' của đạo diễn Hollywood Wolfgang Petersen thực hiện năm 1991.  T&aacute;c phẩm n&agrave;y v&agrave; \"Giao lộ định mệnh\" của anh c&oacute; qu&aacute; nhiều điểm giống  nhau.  <br />&gt; <a class=\"Lead\" href=\"http://vnexpress.net/GL/Van-hoa/San-khau-Dien-anh/2010/09/3BA2072B/\">&lsquo;Giao lộ định mệnh&rsquo; thay đổi diện mạo phim Việt</a>/<a class=\"Lead\" href=\"http://vnexpress.net/GL/Van-hoa/San-khau-Dien-anh/2010/06/3BA1D6B2/\"> \'Giao lộ định mệnh\' đem hơi thở mới cho phim Việt</a></p>\n<p class=\"Normal\">Sau hơn một th&aacute;ng <a class=\"Normal\" href=\"http://vnexpress.net/GL/Van-hoa/San-khau-Dien-anh/2010/09/3BA2026D/\">ra rạp tr&ecirc;n to&agrave;n quốc</a>, bộ phim <em><a href=\"http://search.vnexpress.net/news?s=giao+l%E1%BB%99+%C4%91%E1%BB%8Bnh+m%E1%BB%87nh&amp;g=EB7698D8-B25A-4109-B875-0115B187E1E4\">Giao lộ định mệnh</a> </em>của đạo diễn<em> </em>Victor Vũ bị kh&aacute;n giả ph&aacute;t hiện l&agrave; kh&aacute; giống phim <em>Shattered </em>của Mỹ, ra mắt v&agrave;o năm 1991.</p>\n<p class=\"Normal\">Chiều 20/10, đạo diễn phim c&oacute; cuộc trao đổi với b&aacute;o giới TP HCM để trần t&igrave;nh vụ việc.</p>\n<p class=\"Normal\">- <em>Việc dư luận đang x&ocirc;n xao b&agrave;n t&aacute;n phim \"Giao lộ  định mệnh\" ăn cắp &yacute; tưởng của \"Shattered\" (đạo diễn Mỹ Wolfgang  Petersen), anh giải th&iacute;ch thế n&agrave;o?</em></p>\n<p class=\"Normal\">- Mấy ng&agrave;y qua t&ocirc;i đang bận với dự &aacute;n phim mới của  m&igrave;nh n&ecirc;n kh&ocirc;ng thể trả lời những thắc mắc của nhiều người qua điện thoại  ri&ecirc;ng. Th&ecirc;m v&agrave;o đ&oacute;, khi b&aacute;o ch&iacute; phản &aacute;nh vụ việc, t&ocirc;i chưa thể trả lời  ch&iacute;nh thức v&igrave; th&uacute; thật t&ocirc;i phải t&igrave;m xem bộ phim <em>Shattered</em>. Sau khi đọc qua bảng t&oacute;m tắt nội dung<em> Shattered</em> v&agrave; xem phim tr&ecirc;n youtube, t&ocirc;i thấy quả thực hai phim c&oacute; nhiều điểm rất giống nhau.</p>\n<p class=\"Normal\">T&ocirc;i chỉ mới xem qua <em>Shattered</em> một lần v&agrave; chưa  ph&acirc;n t&iacute;ch kỹ từng ph&acirc;n đoạn để n&ecirc;u ch&iacute;nh x&aacute;c l&agrave; hai phim giống nhau bao  nhi&ecirc;u phần trăm. Nếu n&oacute;i chung về t&igrave;nh huống, nội dung phim hay đường  d&acirc;y c&acirc;u chuyện th&igrave; r&otilde; r&agrave;ng rất giống nhau.</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\n<tbody>\n<tr>\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/1D/F2/Untitled-1to0.jpg\" border=\"1\" alt=\"\" width=\"400\" height=\"616\" /></td>\n</tr>\n<tr>\n<td class=\"Image\">Poster phim \"Shattered\" của Hollywood sản xuất năm 1991.</td>\n</tr>\n</tbody>\n</table>\n<p class=\"Normal\">- <em>Phim \"Shattered\" ra mắt từ năm 1991. C&ograve;n kịch bản \"Giao lộ định mệnh\" được anh viết trong thời gian n&agrave;o? </em></p>\n<p class=\"Normal\">- Từ thời c&ograve;n học điện ảnh ở Mỹ, khoảng năm 1996, t&ocirc;i  đ&atilde; c&oacute; &yacute; tưởng viết một kịch bản phim dựa tr&ecirc;n nguồn cảm hứng từ chữ  \"Inferno\". Từ đ&oacute; h&igrave;nh th&agrave;nh nội dung về một người bị mất tr&iacute; nhớ. Trong  qu&aacute; tr&igrave;nh đi t&igrave;m lại k&yacute; ức, anh ta ph&aacute;t hiện ch&iacute;nh anh ta l&agrave; người g&acirc;y  ra tội &aacute;c v&agrave; phải sống trong địa ngục do m&igrave;nh tạo ra.</p>\n<p class=\"Normal\">L&uacute;c đ&oacute;, khi viết kịch bản n&agrave;y, t&ocirc;i rất t&acirc;m đắc với c&aacute;i  kết bất ngờ m&agrave; m&igrave;nh g&agrave;i v&agrave;o trong c&acirc;u chuyện. T&ocirc;i định l&agrave;m phim ngắn để  tốt nghiệp nhưng do phim tốt nghiệp phải tự m&igrave;nh bỏ kinh ph&iacute; thực hiện  m&agrave; t&ocirc;i chưa đủ khả năng n&ecirc;n đ&atilde; chọn kịch bản kh&aacute;c.</p>\n<p class=\"Normal\">Từ đ&oacute; đến nay, những file vi t&iacute;nh ban đầu t&ocirc;i viết kịch bản <em>Inferno</em> cũng đ&atilde; mất hết. Khi bắt tay v&agrave;o thực hiện bộ phim <em>Giao lộ định mệnh</em>,  t&ocirc;i phải viết lại dựa tr&ecirc;n &yacute; tưởng t&acirc;m đắc từ hơn 10 năm trước của  ch&iacute;nh m&igrave;nh. Một điều t&ocirc;i chắc chắn l&agrave; t&ocirc;i chưa từng xem phim <em>Shattered</em> trước thời điểm viết kịch bản n&agrave;y. C&oacute; thể l&agrave; t&ocirc;i c&oacute; nghe n&oacute;i đến bởi  phim n&agrave;y cũng thuộc dạng c&oacute; tiếng. T&ocirc;i nghĩ sự giống nhau giữa hai bộ  phim l&agrave; một sự tr&ugrave;ng hợp ho&agrave;n to&agrave;n bất ngờ. Thực ra, hai phim vẫn c&oacute;  điểm kh&aacute;c nhau.</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\n<tbody>\n<tr>\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/1D/F2/posetrto.jpg\" border=\"1\" alt=\"\" width=\"400\" height=\"566\" /></td>\n</tr>\n<tr>\n<td class=\"Image\">Poster phim \"Giao lộ định mệnh\".</td>\n</tr>\n</tbody>\n</table>\n<p class=\"Normal\"><em>- Đạo diễn\"Shattered\" ghi r&otilde;, phim dựa tr&ecirc;n tiểu  thuyết của nh&agrave; văn Richard Neely. C&oacute; thể anh cũng đọc qua t&aacute;c phẩm n&agrave;y  v&agrave; bị &aacute;m ảnh bởi c&aacute;c chi tiết trong đ&oacute;?</em></p>\n<p class=\"Normal\">- Hồi trẻ t&ocirc;i cũng đọc truyện trinh th&aacute;m nhiều nhưng  t&ocirc;i chưa bao giờ đọc t&aacute;c phẩm của Richard Neely. T&ocirc;i rất tin tưởng v&agrave;o  tr&iacute; nhớ của m&igrave;nh.</p>\n<p class=\"Normal\">- <em>C&oacute; những chi tiết n&agrave;o giống nhau giữa hai phim khiến anh bất ngờ?</em></p>\n<p class=\"Normal\">- T&ocirc;i xem phim <em>Shattered</em> c&ugrave;ng anh thiết kế,  v&agrave; cả hai ch&uacute;ng t&ocirc;i đều rất bất ngờ v&igrave; c&oacute; những chi tiết m&agrave; ch&uacute;ng t&ocirc;i  chỉ đạo cho diễn vi&ecirc;n hoặc xử l&yacute; ngay tại phim trường <em>Giao lộ định mệnh</em> m&agrave; vẫn giống với phim kia. Những chi tiết như vết sẹo của nh&acirc;n vật,  c&aacute;ch nh&acirc;n vật cầm gậy rồi chuyện ngoại t&igrave;nh với c&ocirc; thư k&yacute;... l&agrave; những  điểm giống khiến t&ocirc;i phải run người. V&igrave; nếu giống đường d&acirc;y c&acirc;u chuyện  trong bộ phim th&igrave; cũng l&agrave; b&igrave;nh thường, nhưng c&ograve;n giống đến từng t&igrave;nh  tiết nhỏ như thế th&igrave; thật bất ngờ.</p>\n<table border=\"0\" cellspacing=\"1\" cellpadding=\"3\" width=\"300\" align=\"center\" bgcolor=\"#408080\">\n<tbody>\n<tr>\n<td class=\"Normal\" align=\"center\" bgcolor=\"#ffffff\">* <a class=\"Normal\" href=\"http://vnexpress.net/GL/Van-hoa/2010/10/3BA21DF2/page_3.asp\">Trailer phim \'Shattered\'</a></td>\n</tr>\n<tr>\n<td class=\"Normal\" align=\"center\" bgcolor=\"#ffffff\">* <a class=\"Normal\" href=\"http://vnexpress.net/GL/Van-hoa/2010/10/3BA21DF2/page_2.asp\">Trailer phim \'Giao lộ định mệnh\'</a></td>\n</tr>\n</tbody>\n</table>\n<p class=\"Normal\">- <em>Anh l&yacute; giải thế n&agrave;o về sự giống nhau \"kỳ lạ\" như thế?</em></p>\n<p class=\"Normal\">- Nếu l&agrave; d&acirc;n trong nghề th&igrave; c&oacute; thể hiểu v&agrave; th&ocirc;ng cảm.  T&ocirc;i từng n&oacute;i nhiều lần, phim n&agrave;y t&ocirc;i l&agrave;m theo phong c&aacute;ch Hollywood v&agrave;  theo trường ph&aacute;i Alfred Hitchcock. Thường th&igrave; phim Hollywood c&oacute; sẵn  những dạng khu&ocirc;n mẫu ri&ecirc;ng của n&oacute;. Từ những khu&ocirc;n mẫu n&agrave;y m&agrave; đường d&acirc;y  v&agrave; t&igrave;nh tiết ph&aacute;t triển c&acirc;u chuyện, nh&acirc;n vật được triển khai. Phim  Hollywood cũng thường c&oacute; v&agrave;i m&ocirc; t&iacute;p v&agrave; c&ocirc;ng thức m&agrave; họ đảo đi đảo lại  rồi đặt th&ecirc;m chi tiết để l&agrave;m hấp dẫn th&ecirc;m.</p>\n<p class=\"Normal\">Điều bất ngờ l&agrave; ngay từ đầu <em>Giao lộ định mệnh</em> v&agrave; <em>Shatttered</em> đ&atilde; giống nhau về &yacute; tưởng v&agrave; bản chất nội dung n&ecirc;n trong c&aacute;i khung sẵn  đ&oacute;, những đường d&acirc;y để ph&aacute;t triển nh&acirc;n vật, ph&aacute;t triển nội dung kh&oacute;  tr&aacute;nh khỏi c&oacute; sự giống nhau.</p>\n<p class=\"Normal\">Nếu ngay từ đầu t&ocirc;i biết về sự giống nhau n&agrave;y hoặc được bạn b&egrave; chỉ ra cho l&agrave; c&oacute; một kịch bản phim <em>Shattered</em> giống phim m&igrave;nh muốn l&agrave;m th&igrave; t&ocirc;i đ&atilde; tr&aacute;nh kh&ocirc;ng l&agrave;m giống nhau đến như thế.</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\n<tbody>\n<tr>\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/1D/F2/anhto.jpg\" border=\"1\" alt=\"\" width=\"485\" height=\"322\" /></td>\n</tr>\n<tr>\n<td class=\"Image\">Một cảnh trong phim \"Giao lộ định mệnh\".</td>\n</tr>\n</tbody>\n</table>\n<p class=\"Normal\"><em>- Vậy theo anh sự kh&aacute;c nhau giữa \"Giao lộ định mệnh\" v&agrave; \"Shattered\" l&agrave; g&igrave;?</em></p>\n<p class=\"Normal\">- Nh&acirc;n vật Mạnh bị mất tr&iacute; nhớ v&agrave; anh ta phải tiếp x&uacute;c  với rất nhiều người để lần t&igrave;m về qu&aacute; khứ của m&igrave;nh. Điểm n&agrave;y kh&aacute;c biệt  với nh&acirc;n vật trong phim <em>Shattered.</em></p>\n<table border=\"1\" cellspacing=\"0\" cellpadding=\"3\" width=\"257\" align=\"right\" bgcolor=\"#eff8f8\">\n<tbody>\n<tr>\n<td>\n<p class=\"Normal\">Irene Trịnh (Nh&agrave; sản xuất phim <em>Giao lộ định mệnh</em>) cho biết: \"Từ khi bắt tay v&agrave;o viết kịch bản để thực hiện <em>Giao lộ định mệnh</em>,  đạo diễn Victor Vũ cũng đ&atilde; tr&ograve; chuyện với t&ocirc;i rất nhiều về &yacute; tưởng thực  hiện bộ phim. Trong qu&aacute; tr&igrave;nh n&agrave;y, ch&uacute;ng t&ocirc;i c&ugrave;ng anh em trong đo&agrave;n l&agrave;m  phim cũng đ&oacute;ng g&oacute;p nhiều &yacute; kiến để Victor Vũ ho&agrave;n th&agrave;nh kịch bản. V&igrave;  thế, khi phim ra mắt th&igrave; đ&acirc;y l&agrave; sản phẩm hợp t&aacute;c của cả đo&agrave;n l&agrave;m phim  chứ kh&ocirc;ng chỉ của ri&ecirc;ng đạo diễn. Từ kịch bản để trở th&agrave;nh một bộ phim  th&igrave; ch&uacute;ng t&ocirc;i cũng phải thay đổi rất nhiều thứ. Giả sử nếu ch&uacute;ng t&ocirc;i  biết trước đ&acirc;y l&agrave; kịch bản phim n&agrave;y giống một phim nổi tiếng n&agrave;o kh&aacute;c  th&igrave; ch&uacute;ng t&ocirc;i rất vui l&ograve;ng để th&ecirc;m d&ograve;ng \"phim x&acirc;y dựng dựa tr&ecirc;n nguồn  cảm hứng từ phim A, B, C... n&agrave;o đ&oacute;.</p>\n<p class=\"Normal\">T&ocirc;i nghĩ sự giống nhau giữa <em>Giao lộ định mệnh</em> v&agrave; <em>Shattered</em> l&agrave; một sự tr&ugrave;ng hợp qu&aacute; bất ngờ.\"</p>\n</td>\n</tr>\n</tbody>\n</table>\n<p class=\"Normal\"><em>- Giả sử, nếu nh&agrave; sản xuất phim Mỹ kiện th&igrave; anh c&oacute; c&aacute;ch n&agrave;o để chứng minh?</em></p>\n<p class=\"Normal\">- T&ocirc;i chưa nghĩ xa đến điều đ&oacute;. T&ocirc;i nghĩ, m&igrave;nh l&agrave;m  trong nghề n&ecirc;n m&igrave;nh hiểu việc cần t&ocirc;n trọng c&aacute;c nh&agrave; l&agrave;m phim kh&aacute;c. Thật  l&agrave; v&ocirc; l&yacute; nếu t&ocirc;i biết c&oacute; một phim như thế m&agrave; lại đi l&agrave;m giống y hệt, thể  hiện sự sao ch&eacute;p một c&aacute;ch r&otilde; r&agrave;ng để mọi người c&oacute; thể ph&aacute;t hiện ra. L&agrave;m  phim l&agrave; một qu&aacute; tr&igrave;nh s&aacute;ng tạo phức tạp. T&ocirc;i đ&atilde; đổ mồ h&ocirc;i, nước mắt cho  <em>Giao lộ định mệnh</em> th&igrave; kh&ocirc;ng thể dễ d&agrave;ng l&agrave;m phim sao ch&eacute;p &yacute; tưởng.</p>\n<p class=\"Normal\">T&ocirc;i c&oacute; cảm gi&aacute;c nếu đo&agrave;n phim b&ecirc;n kia m&agrave; c&oacute; xem <em>Giao lộ định mệnh</em> th&igrave; họ cũng sẽ hiểu v&agrave; th&ocirc;ng cảm cho t&ocirc;i về sự tr&ugrave;ng hợp n&agrave;y.</p>\n<p class=\"Normal\"><em>- Điều n&agrave;y g&acirc;y cản trở g&igrave; cho dự định ph&aacute;t h&agrave;nh phim ở Mỹ?</em></p>\n<p class=\"Normal\">- Thực tế, phim đang trong dự t&iacute;nh ph&aacute;t h&agrave;nh ở Mỹ. Đ&acirc;y  l&agrave; kế hoạch t&ocirc;i đặt ra từ đầu, khi bắt tay thực hiện phim. T&ocirc;i sinh  trưởng v&agrave; học h&agrave;nh ở Mỹ. Thật sự khi t&ocirc;i về nước l&agrave;m phim l&agrave; vừa để phục  vụ kh&aacute;n giả trong nước nhưng cũng đều mong muốn mang phim của m&igrave;nh giới  thiệu với kh&aacute;n giả tại Mỹ. Cho n&ecirc;n, n&oacute;i t&ocirc;i muốn che giấu th&ocirc;ng tin  \"đạo\" phim l&agrave; kh&ocirc;ng đ&uacute;ng.</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\n<tbody>\n<tr>\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/1D/F2/top.jpg\" border=\"1\" alt=\"\" width=\"485\" height=\"351\" /></td>\n</tr>\n<tr>\n<td class=\"Image\">Đạo diễn Victor Vũ (phải) v&agrave; nh&agrave; s&agrave;n xuất Irene Trịnh trong cuộc gặp gỡ b&aacute;o ch&iacute; TP HCM.</td>\n</tr>\n</tbody>\n</table>\n<p class=\"Normal\"><em>- Anh c&ograve;n điều g&igrave; muốn b&agrave;y tỏ với kh&aacute;n giả, những người y&ecirc;u mến \"Giao lộ định mệnh\"?</em></p>\n<p class=\"Normal\">- Thật sự đ&acirc;y l&agrave; một việc hết sức đ&aacute;ng tiếc cho t&ocirc;i. C&ograve;n mọi người nghĩ thế n&agrave;o th&igrave; nằm ngo&agrave;i kiểm so&aacute;t của t&ocirc;i rồi.</p>\n<p class=\"Normal\">L&agrave; một đạo diễn với l&ograve;ng tự trọng nghề nghiệp, t&ocirc;i  kh&ocirc;ng thể n&agrave;o muốn l&agrave;m ra một sản phẩm giống ho&agrave;n to&agrave;n với một phim  kh&aacute;c. Mỗi phim t&ocirc;i l&agrave;m ra đều cố gắng mang v&agrave;o &yacute; tưởng mới lạ của bản  th&acirc;n m&igrave;nh. T&ocirc;i lu&ocirc;n nghi&ecirc;m t&uacute;c trong c&ocirc;ng việc v&igrave; đ&acirc;y l&agrave; đam m&ecirc;. T&ocirc;i  mong mọi người hiểu t&ocirc;i kh&ocirc;ng thể n&agrave;o bỏ ra biết bao c&ocirc;ng sức v&agrave; thời  gian để l&agrave;m một phim qu&aacute; giống một phim kh&aacute;c như thế, giống đến nỗi mỗi  người c&oacute; thể ph&aacute;t hiện ra một c&aacute;ch dễ d&agrave;ng.</p>\n<p class=\"Normal\">Ph&aacute;n x&eacute;t v&agrave; quyết định l&agrave; của kh&aacute;n giả chứ kh&ocirc;ng c&ograve;n nằm ở t&ocirc;i nữa.</p>','2010-10-21 15:27:19','ADMIN',31,1),(6,'messi-lap-dai-cong-giup-barca-chiem-ngoi-dau-bang','Messi lập đại công giúp Barca chiếm ngôi đầu bảng','http://localhost/mycms/images/upload/1287646690_mes2.jpg','Tiền đạo người Argentina ghi cả hai bàn trong trận Barca thắng Copenhagen với kết quả 2-0 ở vòng bảng Champions League tối thứ 4.','<p class=\"Lead\">Tiền đạo người Argentina ghi cả hai b&agrave;n trong trận Barca  thắng Copenhagen với kết quả 2-0 ở v&ograve;ng bảng Champions League tối thứ  tư.</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\n<tbody>\n<tr>\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/1E/21/mes.jpg\" border=\"1\" alt=\"Busquets v&agrave; Abidal ch&uacute;c mừng Messi sau pha ấn định tỷ số.\" width=\"399\" height=\"298\" /></td>\n</tr>\n<tr>\n<td class=\"Image\">Busquets v&agrave; Abidal ch&uacute;c mừng Messi sau pha ấn định tỷ số.</td>\n</tr>\n</tbody>\n</table>\n<p class=\"Normal\">Hai b&agrave;n của Messi chỉ l&agrave; th&agrave;nh quả của hai trong số nhiều cơ hội d&agrave;nh cho đội chủ nh&agrave; trong trận đấu tại s&acirc;n Nou Camp.</p>\n<table border=\"0\" cellspacing=\"1\" cellpadding=\"3\" width=\"250\" align=\"right\" bgcolor=\"#408080\">\n<tbody>\n<tr>\n<td class=\"PnDTitle\" align=\"center\" bgcolor=\"#408080\">Lượt trận thứ ba v&ograve;ng bảng</td>\n</tr>\n<tr>\n<td class=\"PnLink\" align=\"left\" bgcolor=\"#ffffff\">Thứ ba, ng&agrave;y 19/10 <br />Bảng E<br /><strong>Roma 1-3 Basel</strong><br />(Borriello 21\' - Frei 12\', Inkoom 44\', Cabral 90\'+3)<br /><strong>Bayern 3-2 Cluj</strong><br />(Cadu phản lưới nh&agrave; 32\', Panin phản lưới nh&agrave; 37\', Gomez 77\' - Cadu 28\', Culio 86\')<br /><br />Bảng F<br /><strong>Spartak Moscow 0-2 Chelsea</strong><br />(Zhirkov 23\', Anelka 43\')<br /><strong>Marseille 1-0 Zilina</strong><br />(Diawara 48\')<br /><br />Bảng G<br /><strong>Real Madrid 2-0 Milan</strong><br />(Ronaldo 13\', Oezil 14\')<br /><strong>Ajax 2-1 Auxerre</strong><br />(De Zeeuw 7\', Suarez 41\' - Birsa 56\')<br /><br />Bảng H<br /><strong>Arsenal 5-1 Shakhtar Donetsk</strong><br />(Song 19\', Nasri 42\', Fabregas pen 60\', Wilshere 66\', Chamakh 69\' - Eduardo 82\')<br /><strong>Braga 2-0 Partizan Belgrade</strong><br />(Lima 35\', Matheus 90\')<br /><br />Thứ tư, ng&agrave;y 20/10 <br />Bảng A<br /><strong>Inter 4-3 Tottenham</strong> <br />(Zanetti 2\', Eto\'o 11\' pen, 35\', Stankovic 14\' - Bale 52\', 90\', 90\'+1)<br /><strong>Twente 1-1 Bremen<br /></strong>(Janssen 75\' - Arnautovic 80\')<br /><br />Bảng B <br /><strong>Schalke 3-1 Hapoel Tel Aviv </strong><br />(Raul 3\', 58\', Jurado 68\' -Shechter 90\'+4)<br /><strong>Lyon 2-0 Benfica<br /></strong>(Briand 21\', Lopez 51\')<br /><br />Bảng C <br /><strong>Rangers 1-1 Valencia <br /></strong>(Edu 34\' - Edu 46\' phản lưới)<br /><strong>MU 1-0 Bursaspor</strong> <br />(Nani 7\')<br /><br />Bảng D<br /><strong>Panathinaikos 0-0 Rubin Kazan </strong><br /><strong>Barca 2-0 Copenhagen<br /></strong>(Messi 19\', 90\'+2\')</td>\n</tr>\n</tbody>\n</table>\n<p class=\"Normal\">B&agrave;n đầu ti&ecirc;n được Messi thực hiện ở ph&uacute;t 19 bằng một  c&uacute; s&uacute;t xa v&agrave;o g&oacute;c cao. Sau khi Barca trải qua hiệp hai kh&aacute; căng thẳng,  b&agrave;n thứ hai được ghi trong khoảng thời gian đ&aacute; b&ugrave;.</p>\n<p class=\"Normal\">Thủ m&ocirc;n Wiland đ&atilde; cứu thua cho Copenhagen trước một  loạt pha dứt điểm của Messi, David Villa v&agrave; Daniel Alves, nhưng tiền đạo  đội kh&aacute;ch Cesar Martin cũng bỏ lỡ kh&ocirc;ng &iacute;t cơ hội tốt c&ograve;n đồng đội Dame  N&rsquo;Doye c&oacute; một c&uacute; s&uacute;t bật x&agrave;.</p>\n<p class=\"Normal\">Chiến thắng tối qua đ&atilde; đem lại vị tr&iacute; dẫn đầu bảng D  v&agrave; 7 điểm cho đội b&oacute;ng T&acirc;y Ban Nha, hơn Copenhagen 1 điểm. Rubin Kazan  c&oacute; 2 điểm v&agrave; Panathinaikos c&oacute; 1 điểm sau khi hai đội n&agrave;y h&ograve;a trong trận  c&ograve;n lại.</p>\n<p class=\"Normal\">So với trận thắng Valencia 2-1 cuối tuần trước, trận  đấu được HLV Pep Guardiola ca ngợi l&agrave; \"cuộc lột x&aacute;c của đội b&oacute;ng\", Barca  đ&atilde; c&oacute; nhiều thay đổi trong danh s&aacute;ch xuất ph&aacute;t. Thủ m&ocirc;n dự bị Jose  Manuel Pinto bắt thay Victor Valdes, người bị chấn thương nhẹ ở đầu gối.  Xavi Hernandez chưa hết đau g&acirc;n kheo v&agrave; được Guardiola cho ngồi ghế dự  bị. Đ&aacute; thay tiền vệ \"nhạc trưởng\" của Barca l&agrave; t&acirc;n binh Javier  Mascherano.</p>\n<p class=\"Normal\">Với lối chơi chuyền ban ngắn sở trường, Barca nhanh  ch&oacute;ng chiếm ưu thế nhưng đội kh&aacute;ch kh&ocirc;ng bị cho&aacute;ng ngợp. Bầu kh&ocirc;ng kh&iacute;  tại s&acirc;n Nou Camp trở n&ecirc;n s&ocirc;i động từ ph&uacute;t thứ tư, khi Villa s&uacute;t tr&uacute;ng x&agrave;  ngang Copenhagen. Villa l&agrave; người ghi b&agrave;n trong cả hai trận trước ở v&ograve;ng  bảng.</p>\n<p class=\"Normal\">Thủ m&ocirc;n Wiland tỏ ra xuất sắc trong t&igrave;nh huống v&ocirc; hiệu  h&oacute;a cơ hội của Maxwell v&agrave; Messi ở ph&uacute;t 17, nhưng anh kh&ocirc;ng cản được c&uacute;  s&uacute;t như đại b&aacute;c của Messi ở ph&uacute;t 19.</p>\n<p class=\"Normal\">Trong t&igrave;nh huống đ&oacute;n đường chuyền của Andres Iniesta,  tiền đạo người Argentina dứt điểm quyết đo&aacute;n từ cự ly 25 m khiến b&oacute;ng  bay v&agrave;o g&oacute;c cao. Đ&acirc;y l&agrave; b&agrave;n thứ ba của Messi ở Champions League  2010-2011, đồng thời l&agrave; pha thủng lưới đầu ti&ecirc;n của Copenhagen, khiến kế  hoạch ph&ograve;ng ngự chiều s&acirc;u kết hợp phản c&ocirc;ng của họ bị ph&aacute; sản.</p>\n<p class=\"Normal\">Ph&uacute;t 32, Wiland tiếp tục từ chối nỗ lực dứt điểm của  Villa. Messi c&oacute; th&ecirc;m một lần đưa b&oacute;ng v&agrave;o lưới đối phương trong hiệp một  nhưng bị trọng t&agrave;i thổi việt vị.</p>\n<p class=\"Normal\">Sau khi kh&ocirc;ng tung được c&uacute; s&uacute;t ra hồn n&agrave;o trong hiệp  một th&igrave; Copenhagen bỗng chơi lột x&aacute;c sau giờ nghỉ. Trong l&uacute;c Barca cố  gắng giải quyết trận đấu, đội kh&aacute;ch đ&atilde; chứng tỏ năng lực tổ chức ph&ograve;ng  ngự v&agrave; c&oacute; nhiều phương &aacute;n phản c&ocirc;ng đa dạng.</p>\n<p class=\"Normal\">Ph&uacute;t 48, N&rsquo;Doye bỏ lỡ cơ hội đ&aacute;nh đầu sau một quả tạt  như đặt. Ph&uacute;t 67, cầu thủ n&agrave;y tiếp tục uy hiếp khung th&agrave;nh thủ m&ocirc;n Jose  Pinto. C&uacute; s&uacute;t đầu ti&ecirc;n tưởng sẽ th&agrave;nh c&ocirc;ng th&igrave; b&oacute;ng dội x&agrave; ngang. Trong  khi Pinto mất đ&agrave; v&agrave; khung th&agrave;nh bị bỏ trống, Cesar Martin đ&atilde; đ&aacute;nh đầu  bồi ra ngo&agrave;i.</p>\n<p class=\"Normal\">HLV Guardiola sau đ&oacute; buộc phải tung Xavi v&agrave; tiền đạo  Pedro v&agrave;o s&acirc;n. Ph&uacute;t 74 Dani Alves c&oacute; c&uacute; xoạc b&oacute;ng kịp thời giải nguy cho  Barca trong t&igrave;nh huống uy hiếp của Christian Bolanos. Hậu vệ người  Brazil c&ograve;n c&oacute; c&uacute; s&uacute;t đập cột dọc của Copenhagen ở ph&uacute;t 87.</p>\n<p class=\"Normal\">Sau khi trận đấu tưởng chừng an b&agrave;i với tỷ số 1-0,  trong t&igrave;nh huống tấn c&ocirc;ng ở những ph&uacute;t cuối, Messi ghi được b&agrave;n ấn định  chiến thắng. Tiền đạo người Argentina dứt điểm dễ d&agrave;ng sau khi Eric  Abidal c&oacute; c&uacute; v&ocirc;l&ecirc; kh&ocirc;ng ch&iacute;nh x&aacute;c, v&ocirc; t&igrave;nh biến th&agrave;nh đường chuyền tốt  cho đồng đội.</p>\n<p class=\"Normal\"><strong><a href=\"http://vnexpress.net/GL/The-thao/2010/10/3BA21E21/Page_2.asp\">Xem b&agrave;n mở tỷ số của Messi</a></strong>.</p>\n<p class=\"Normal\"><strong><a href=\"http://vnexpress.net/GL/The-thao/2010/10/3BA21E21/Page_3.asp\">Messi ấn định chiến thắng</a></strong>.</p>\n<p class=\"Normal\"><strong><span style=\"color: #4f4f4f;\">Đội h&igrave;nh thi đấu:</span></strong></p>\n<p class=\"Normal\"><strong><span style=\"color: #4f4f4f;\">Barcelona:</span></strong> Pinto; Alves, Piqu&eacute;, Puyol, Abidal; Mascherano, Busquets, Iniesta (Keita, 89\'), Maxwell (Pedro, 72\'); Villa (Xavi, 72\'), Messi.</p>\n<p class=\"Normal\"><strong><span style=\"color: #4f4f4f;\">Copenhague:</span></strong> Wiland; Pospech, Antonsson, Zanka Jorgensen, Wendt (Larsson, 89\');  Vingaard (Bola&ntilde;os, 62\'), Claudemir, Kvist, Gronkjaer; Santin (Zohore,  74\'), N\'Doye.</p>\n<p class=\"Normal\"><strong><span style=\"color: #4f4f4f;\">Bảng D</span></strong></p>\n<p><strong>&nbsp;</strong></p>\n<table border=\"1\" cellspacing=\"1\" cellpadding=\"1\" width=\"300\" align=\"center\">\n<tbody>\n<tr>\n<td class=\"Normal\" align=\"middle\"><strong><span style=\"color: #3f3f3f;\">TT</span></strong></td>\n<td class=\"Normal\" align=\"middle\"><strong><span style=\"color: #3f3f3f;\">Đội </span></strong></td>\n<td class=\"Normal\" align=\"middle\"><strong><span style=\"color: #3f3f3f;\">Trận</span></strong></td>\n<td class=\"Normal\" align=\"middle\"><strong><span style=\"color: #3f3f3f;\">T</span></strong></td>\n<td class=\"Normal\" align=\"middle\"><strong><span style=\"color: #3f3f3f;\">H</span></strong></td>\n<td class=\"Normal\" align=\"middle\"><strong><span style=\"color: #3f3f3f;\">B</span></strong></td>\n<td class=\"Normal\" align=\"middle\"><strong><span style=\"color: #3f3f3f;\">H/S</span></strong></td>\n<td class=\"Normal\" align=\"middle\"><strong><span style=\"color: #3f3f3f;\">Điểm</span></strong></td>\n</tr>\n<tr>\n<td class=\"Normal\" align=\"middle\">1</td>\n<td class=\"Normal\" align=\"middle\">Barcelona</td>\n<td class=\"Normal\" align=\"middle\">3</td>\n<td class=\"Normal\" align=\"middle\">2</td>\n<td class=\"Normal\" align=\"middle\">1</td>\n<td class=\"Normal\" align=\"middle\">0</td>\n<td class=\"Normal\" align=\"middle\">6</td>\n<td class=\"Normal\" align=\"middle\">7</td>\n</tr>\n<tr>\n<td class=\"Normal\" align=\"middle\">2</td>\n<td class=\"Normal\" align=\"middle\">Copenhagen</td>\n<td class=\"Normal\" align=\"middle\">3</td>\n<td class=\"Normal\" align=\"middle\">2</td>\n<td class=\"Normal\" align=\"middle\">0</td>\n<td class=\"Normal\" align=\"middle\">1</td>\n<td class=\"Normal\" align=\"middle\">1</td>\n<td class=\"Normal\" align=\"middle\">6</td>\n</tr>\n<tr>\n<td class=\"Normal\" align=\"middle\">3</td>\n<td class=\"Normal\" align=\"middle\">Rubin Kazan</td>\n<td class=\"Normal\" align=\"middle\">3</td>\n<td class=\"Normal\" align=\"middle\">0</td>\n<td class=\"Normal\" align=\"middle\">2</td>\n<td class=\"Normal\" align=\"middle\">1</td>\n<td class=\"Normal\" align=\"middle\">-1</td>\n<td class=\"Normal\" align=\"middle\">2</td>\n</tr>\n<tr>\n<td class=\"Normal\" align=\"middle\">4</td>\n<td class=\"Normal\" align=\"middle\">Panathinaikos</td>\n<td class=\"Normal\" align=\"middle\">3</td>\n<td class=\"Normal\" align=\"middle\">0</td>\n<td class=\"Normal\" align=\"middle\">1</td>\n<td class=\"Normal\" align=\"middle\">2</td>\n<td class=\"Normal\" align=\"middle\">-6</td>\n<td class=\"Normal\" align=\"middle\">1</td>\n</tr>\n</tbody>\n</table>\n<p class=\"Normal\" style=\"text-align: right;\"><strong>Th&uacute;y An</strong></p>','2010-10-22 16:53:53','nclong',123,1),(7,'bep-com-cuu-doi-cho-nguoi-dan-o-ron-lu','Bếp cơm cứu đói cho người dân ở rốn lũ','http://localhost/mycms/images/upload/1287647107_cuu1.jpg','Từ 3 ngày nay, những chiếc xuống nhỏ mang theo rổ cơm nắm lặng lẽ vượt qua biển nước tới từng gia đình tại các xã ngập sâu ở huyện Vũ Quang, Hà Tĩnh. Những nắm cơm được trao vội trong cái nhìn biết ơn của người dân.','<p class=\"Lead\">Từ 3 ng&agrave;y nay, những chiếc xuống nhỏ mang theo rổ cơm  nắm lặng lẽ vượt qua biển nước tới từng gia đ&igrave;nh tại c&aacute;c x&atilde; ngập s&acirc;u ở  huyện Vũ Quang, H&agrave; Tĩnh. Những nắm cơm được trao vội trong c&aacute;i nh&igrave;n biết  ơn của người d&acirc;n.<br />&gt; <a class=\"Lead\" href=\"http://vnexpress.net/GL/Doi-song/2010/10/3BA21D6C/\">Chật vật kiếm ăn giữa biển nước miền Trung</a></p>\n<p class=\"Normal\">\"Sau nhiều ng&agrave;y người d&acirc;n v&ugrave;ng ngập chỉ sống cầm hơi  bằng m&igrave; g&oacute;i v&agrave; lương kh&ocirc;, huyện đ&atilde; đưa ra s&aacute;ng kiến nấu cơm nắm đi ph&aacute;t  cứu tế. Nh&igrave;n những đứa trẻ hồ hởi mở g&oacute;i cơm ăn, hạt d&iacute;nh đầy tr&ecirc;n m&aacute; m&agrave;  ch&uacute;ng t&ocirc;i rơi nước mắt\", &ocirc;ng Nguyễn Hồng Lĩnh, Ph&oacute; B&iacute; thư Huyện ủy  huyện Vũ Quang, một trong những điểm ngập s&acirc;u nhất của H&agrave; Tĩnh, cho  biết.</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"247\" align=\"center\" bgcolor=\"#e2c5c5\">\n<tbody>\n<tr>\n<td class=\"Normal\" align=\"center\"><a class=\"Normal\" href=\"http://vnexpress.net/GL/Doi-song/2010/10/3BA21E6F/page_2.asp\"><strong>Clip nắm cơm cứu đ&oacute;i v&ugrave;ng rốn lũ</strong></a></td>\n</tr>\n</tbody>\n</table>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"100%\">\n<tbody>\n<tr>\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/1E/6F/nau1.jpg\" border=\"1\" alt=\"\" width=\"490\" height=\"392\" /></td>\n</tr>\n<tr>\n<td class=\"Image\">S&aacute;ng kiến nấu cơm nắm đi cứu tế đến từng hộ được đưa  ra mới đ&acirc;y đ&atilde; được hưởng ứng rộng r&atilde;i. Lực lượng tham gia l&agrave; chị em phụ  nữ ở c&aacute;c x&atilde; kh&ocirc;ng bị ngập trong huyện Vũ Quang.</td>\n</tr>\n<tr>\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/1E/6F/nau6.jpg\" border=\"1\" alt=\"\" width=\"490\" height=\"385\" /></td>\n</tr>\n<tr>\n<td class=\"Image\">C&aacute;c bếp nấu được đặt ở những nơi kh&ocirc; r&aacute;o, hoặc tại nơi  ngập &iacute;t. Cơm sau khi nấu được nắm th&agrave;nh từng g&oacute;i, v&agrave; được chở xuồng đi  ph&aacute;t đến từng hộ d&acirc;n.</td>\n</tr>\n<tr>\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/1E/6F/nau3.jpg\" border=\"1\" alt=\"\" width=\"490\" height=\"348\" /></td>\n</tr>\n<tr>\n<td class=\"Image\">C&aacute;c x&atilde; nhận được cơm nắm l&agrave; những nơi ngập s&acirc;u trong  huyện Vũ Quang như Hương Nguy&ecirc;n, Đức Li&ecirc;n, Đức Bồng, Đức Hương. Nhiều  người trong số họ đ&atilde; kh&ocirc;ng biết đến miếng cơm cả tuần nay.</td>\n</tr>\n<tr>\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/1E/6F/nau4.jpg\" border=\"1\" alt=\"\" width=\"490\" height=\"371\" /></td>\n</tr>\n<tr>\n<td class=\"Image\">C&aacute;c b&agrave;, c&aacute;c chị qu&acirc;y quần b&ecirc;n nắm cơm ấm l&ograve;ng.</td>\n</tr>\n<tr>\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/1E/6F/nau5.jpg\" border=\"1\" alt=\"\" width=\"490\" height=\"360\" /></td>\n</tr>\n<tr>\n<td class=\"Image\">\"Nhận được nắm cơm ấm l&ograve;ng, người d&acirc;n hết sức vui mừng v&agrave; cảm động\", &ocirc;ng Nguyễn Hồng Lĩnh cho biết.</td>\n</tr>\n<tr>\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/1E/6F/nau2.jpg\" border=\"1\" alt=\"\" width=\"490\" height=\"387\" /></td>\n</tr>\n<tr>\n<td class=\"Image\">Ngo&agrave;i c&aacute;c bếp nấu cơm nắm đi cứu tế, một số bếp d&atilde;  chiến hoặc bếp tập thể cũng được dựng l&ecirc;n ở ngay c&aacute;c x&atilde; n&agrave;y, cung cấp  bữa ăn chủ yếu cho người gi&agrave; v&agrave; trẻ nhỏ.</td>\n</tr>\n</tbody>\n</table>\n<p class=\"Normal\" style=\"text-align: right;\"><strong>Nguy&ecirc;n Khoa - Nguyễn Hưng</strong></p>','2010-10-21 15:45:37','ADMIN',18,1),(8,'nhung-chuyen-than-bi-quanh-nguoi-rung-o-trung-quoc','Những chuyện thần bí quanh \'người rừng\' ở Trung Quốc','http://localhost/mycms/images/upload/1287647165_yk2.jpg','Nhiều nhân chứng kể rằng các nữ dã nhân tại Trung Quốc đột nhập vào một số làng trong rừng để tìm kiếm đàn ông.','<p class=\"Lead\">Nhiều nh&acirc;n chứng kể rằng c&aacute;c nữ d&atilde; nh&acirc;n tại Trung Quốc đột nhập v&agrave;o một số l&agrave;ng trong rừng để t&igrave;m kiếm đ&agrave;n &ocirc;ng.</p>\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\n<tbody>\n<tr>\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/1D/FF/Yeren2.jpg\" alt=\"C&aacute;c nh&agrave; khoa học t&igrave;m thấy \" width=\"450\" height=\"281\" /></td>\n</tr>\n<tr>\n<td class=\"Image\">C&aacute;c nh&agrave; khoa học t&igrave;m thấy \"l&ocirc;ng d&atilde; nh&acirc;n\" trong rừng Thần N&ocirc;ng Gia, tỉnh Hồ Bắc, Trung Quốc trong thập ni&ecirc;n 80. Ảnh: <em>Xinhua</em>.</td>\n</tr>\n</tbody>\n</table>\n<p class=\"Normal\">Hơn 400 người Trung Quốc khẳng định họ từng nh&igrave;n thấy  d&atilde; nh&acirc;n - sinh vật giống người hoặc đười ươi song lại c&oacute; chiều cao tới 2  m - trong rừng Thần N&ocirc;ng Gia thuộc tỉnh Hồ Bắc. Chẳng hạn, v&agrave;i người kể  rằng c&aacute;c d&atilde; nh&acirc;n nữ đột nhập v&agrave;o một số l&agrave;ng trong rừng để ngủ với đ&agrave;n  &ocirc;ng. Năm 1983, tờ <em>Nhật b&aacute;o Chiết Giang</em> đưa tin một n&ocirc;ng  d&acirc;n đang ngủ trong chiếc l&aacute;n trong rừng th&igrave; một sinh vật to lớn c&oacute; h&igrave;nh  dạng giống phụ nữ lao v&agrave;o l&aacute;n v&agrave; định thực hiện h&agrave;nh vi giao cấu với  anh. Sinh vật đ&oacute; c&oacute; cặp mắt m&agrave;u xanh dương thẫm.</p>\n<p class=\"Normal\">&ldquo;Người n&ocirc;ng d&acirc;n kh&ocirc;ng k&ecirc;u được v&igrave; qu&aacute; sợ h&atilde;i. Anh cũng  kh&ocirc;ng thể chống cự được đối thủ. Nữ d&atilde; nh&acirc;n kia giao cấu với anh trong  v&agrave;i ph&uacute;t rồi bỏ đi&rdquo;, tờ b&aacute;o kể.</p>\n<p class=\"Normal\">Lời đồn đại về sự tồn tại của d&atilde; nh&acirc;n khiến hơn 100  nh&agrave; khoa học th&agrave;nh lập Hiệp hội T&igrave;m kiếm d&atilde; nh&acirc;n Hồ Bắc v&agrave;o th&aacute;ng  11/2009. Wang Shancai, một ph&oacute; chủ tịch của hiệp hội, cho biết, c&aacute;c nh&agrave;  khoa học đang quy&ecirc;n tiền để thực hiện một cuộc t&igrave;m kiếm d&atilde; nh&acirc;n quy m&ocirc;  lớn trong rừng Thần N&ocirc;ng Gia. Theo &ocirc;ng Wang, nếu d&atilde; nh&acirc;n thực sự tồn  tại, ch&uacute;ng sẽ gi&uacute;p giới khoa học hiểu r&otilde; hơn qu&aacute; tr&igrave;nh động vật linh  trưởng tiến h&oacute;a th&agrave;nh người.</p>\n<p class=\"Normal\">Viện Khoa học Trung Quốc từng cử ba đo&agrave;n chuy&ecirc;n gia  v&agrave;o rừng Thần N&ocirc;ng Gia để t&igrave;m d&atilde; nh&acirc;n trong thập ni&ecirc;n 70 v&agrave; 80. Họ t&igrave;m  thấy nhiều thứ được cho l&agrave; của d&atilde; nh&acirc;n - như l&ocirc;ng, ph&acirc;n, dấu ch&acirc;n v&agrave; chỗ  ở - song kh&ocirc;ng đưa ra kết luận ch&iacute;nh thức về sự tồn tại của ch&uacute;ng. Wang  khẳng định ba đo&agrave;n chuy&ecirc;n gia trước đ&acirc;y đ&atilde; phung ph&iacute; thời gian v&agrave; c&ocirc;ng  sức do t&igrave;m kiếm tr&ecirc;n phạm vi qu&aacute; rộng. Ngo&agrave;i ra thời đ&oacute; họ cũng kh&ocirc;ng c&oacute;  những thiết bị c&ocirc;ng nghệ ti&ecirc;n tiến. Cuộc t&igrave;m kiếm sắp tới sẽ chỉ tập  trung trong phạm vi hẹp, như c&aacute;c hang m&agrave; d&atilde; nh&acirc;n c&oacute; thể đang sống.</p>\n<p class=\"Normal\">&ldquo;Ch&uacute;ng t&ocirc;i sẽ chia th&agrave;nh 5 đo&agrave;n để tập trung t&igrave;m kiếm  tại 5 khu vực quan trọng m&agrave; d&atilde; nh&acirc;n c&oacute; thể xuất hiện. C&aacute;c đo&agrave;n sẽ sử  dụng những biện ph&aacute;p hiện đại nhất, bao gồm cả bẫy ảnh. Với sự hỗ trợ  của c&aacute;c thiết bị hiện đại nhất, t&ocirc;i tin khả năng t&igrave;m thấy d&atilde; nh&acirc;n l&agrave; 80%  trở l&ecirc;n&rdquo;, Wang n&oacute;i.</p>\n<p class=\"Normal\">Nhiều nh&agrave; khoa học tại Trung Quốc v&agrave; tr&ecirc;n thế giới cho  rằng, d&atilde; nh&acirc;n m&agrave; hơn 400 người từng thấy c&oacute; thể chỉ l&agrave; một lo&agrave;i đười  ươi m&agrave; con người chưa biết.</p>\n<p class=\"Normal\">Nh&agrave; b&aacute;o Mỹ Nicholas Redfern, một trong những chuy&ecirc;n  gia về sinh vật lạ h&agrave;ng đầu thế giới, cho rằng nỗ lực của giới khoa học  Trung Quốc kh&ocirc;ng phải &yacute; tưởng viển v&ocirc;ng, bởi nhiều h&oacute;a thạch cho thấy d&atilde;  nh&acirc;n c&oacute; thể tồn tại.</p>\n<p class=\"Normal\">&ldquo;Rất nhiều c&acirc;u chuyện về qu&aacute;i vật khổng lồ được người  d&acirc;n bịa ra dựa tr&ecirc;n những c&acirc;u chuyện cổ t&iacute;ch hoặc truyền thuyết. Nhưng  thật ra người ta đ&atilde; t&igrave;m thấy nhiều h&oacute;a thạch cho thấy những sinh vật lớn  giống người từng sống tại Trung Quốc hơn 300.000 năm trước&rdquo;, Redfern  n&oacute;i với <em>Fox News</em>.</p>\n<p class=\"Normal\">C&aacute;c chuy&ecirc;n gia linh trưởng từng t&igrave;m thấy xương h&agrave;m,  răng v&agrave; nhiều mảnh xương kh&aacute;c của một động vật c&oacute; h&igrave;nh dạng giống người  trong rừng Thần N&ocirc;ng Gia. Sinh vật n&agrave;y c&oacute; chiều cao xấp xỉ 2,7 m. C&aacute;c  nh&agrave; khoa học gọi n&oacute; l&agrave; <em>Gigantopithecus</em>.</p>\n<p class=\"Normal\">Redfern đ&atilde; tham gia v&ocirc; số cuộc t&igrave;m kiếm người rừng khổng lồ song c&aacute;c nỗ lực đ&oacute; đều thất bại v&igrave; thiếu tiền.</p>\n<p class=\"Normal\">&ldquo;Chẳng c&oacute; g&igrave; tuyệt vời hơn nếu c&aacute;c nh&agrave; khoa học c&oacute; đủ  tiền để t&igrave;m kiếm sinh vật b&iacute; ẩn trong một năm. Nếu bạn chỉ v&agrave;o rừng  trong một tuần v&agrave; t&igrave;m kiếm một c&aacute;ch ngẫu nhi&ecirc;n th&igrave; cơ hội t&igrave;m thấy người  khổng lồ sẽ rất mong manh&rdquo;, &ocirc;ng n&oacute;i.</p>\n<p class=\"Normal\">Song nếu Hiệp hội T&igrave;m kiếm người rừng tỉnh Hồ Bắc  quy&ecirc;n đủ tiền để thực hiện cuộc t&igrave;m kiếm l&acirc;u d&agrave;i th&igrave; một c&acirc;u hỏi đặt ra:  Người khổng lồ c&ograve;n sống trong c&aacute;c khu rừng ch&acirc;u &Aacute; kh&ocirc;ng?</p>\n<p class=\"Normal\">&ldquo;C&aacute;c bằng chứng khoa học cho thấy <em>Gigantopithecus </em>đ&atilde;  tuyệt chủng từ l&acirc;u, nhưng c&aacute;c nh&agrave; khoa học cũng thường xuy&ecirc;n mắc sai  lầm. Kết luận cuối c&ugrave;ng của c&aacute;c nh&agrave; khoa học kh&ocirc;ng phải bao giờ cũng  đ&uacute;ng&rdquo;, Redfern ph&aacute;t biểu.</p>\n<p class=\"Normal\" style=\"text-align: right;\"><strong>Minh Long</strong></p>','2010-10-22 15:14:10','nclong',34,1),(9,'test','test','http://localhost/mycms/images/upload/1287645742_2a.jpg','test','<p>test</p>','2010-10-22 16:54:42','nclong',0,0),(10,'test','test','','','','2010-10-22 17:10:23','nclong',0,0),(11,'tong-cong-ty-giau-khi-vn','tổng cồng ty giầu khí VN','http://localhost/mycms/images/upload/1287647107_cuu1.jpg','helo','<p>t&eacute;t thử c&aacute;i n&agrave;o</p>','2010-11-16 01:03:35','admin',1,0),(12,'ky-nang-tim-viec-part-time','Kỹ năng tìm việc part-time','http://localhost/mycms/images/upload/1287474677_e2nz.png','Nhu cầu về việc làm thêm hay còn gọi là việc part-time khá lớn trong đời sống. Từ học sinh, sinh viên, những người đã nghỉ hưu hay cả những người đang đi làm nhưng muốn kiếm thêm thu nhập ngoài giờ hành chính. Tuy nhiên, để thực hiện mong muốn không phải là dễ.','<p><strong>Nhu cầu về việc l&agrave;m th&ecirc;m hay c&ograve;n gọi l&agrave;  việc part-time kh&aacute; lớn trong đời sống. Từ học sinh, sinh vi&ecirc;n, những  người đ&atilde; nghỉ hưu hay cả những người đang đi l&agrave;m nhưng muốn kiếm th&ecirc;m  thu nhập ngo&agrave;i giờ h&agrave;nh ch&iacute;nh. Tuy nhi&ecirc;n, để thực hiện mong muốn kh&ocirc;ng  phải l&agrave; dễ.</strong><br /> <br /></p>\n<div><img style=\"max-width: 800px; cursor: pointer;\" src=\"http://dantri2.vcmedia.vn/ThumbImages/Uploaded/2010/03/10/job-100310-1_130.jpg\" border=\"0\" alt=\"\" /></div>\n<p><br /> 1. Lu&ocirc;n sẵn s&agrave;ng<br /> <br /> Bạn n&ecirc;n chuẩn bị một chiếc điện thoại cố định c&oacute; chức năng trả lời v&agrave;  ghi lại lời nhắn của người gọi tới khi kh&ocirc;ng c&oacute; ai trực m&aacute;y. Điều n&agrave;y  rất c&oacute; &iacute;ch trong trường hợp nh&agrave; tuyển dụng muốn li&ecirc;n hệ với bạn. B&ecirc;n  cạnh đ&oacute;, h&atilde;y chuẩn bị tinh thần để lu&ocirc;n sẵn s&agrave;ng với những cuộc phỏng  vấn nhanh để c&oacute; thể nhận được c&ocirc;ng việc ngay l&uacute;c đ&oacute;.<br /> <br /> <br /> <br /> 2. Sử dụng c&ocirc;ng cụ t&igrave;m kiếm online<br /> <br /> Tr&ecirc;n mạng hiện nay c&oacute; kh&aacute; nhiều website cung cấp việc l&agrave;m part-time như  24h.com.vn, kiemviec.com, tuyendung.com,v.v. Tất cả những trang n&agrave;y đều  c&oacute; ph&acirc;n loại c&aacute;c c&ocirc;ng việc theo ca, theo giờ v&agrave; c&oacute; c&ocirc;ng cụ lọc kh&aacute; hiệu  quả theo từng nhu cầu của ứng vi&ecirc;n. Với nhiều trang web, bạn c&oacute; thể nộp  đơn trực tuyến tới nh&agrave; tuyển dụng.<br /> <br /> <br /> <br /> 3. Nộp hồ sơ trực tuyến<br /> <br /> C&oacute; kh&ocirc;ng &iacute;t nh&agrave; tuyển dụng của c&aacute;c c&ocirc;ng việc part-time chấp nhận h&igrave;nh  thức nộp hồ sơ online. Do đ&oacute;, với những c&ocirc;ng việc quan t&acirc;m, bạn cần xem  x&eacute;t xem m&igrave;nh c&oacute; thể nộp hồ sơ bằng h&igrave;nh thức n&agrave;y kh&ocirc;ng.<br /> <br /> <br /> <br /> 4. Nộp g&igrave; trong hồ sơ trực tuyến?<br /> <br /> Hầu hết trong c&aacute;c hồ sơ xin việc trực tuyến, nh&agrave; tuyển dụng đều mong  muốn c&oacute; được th&ocirc;ng tin li&ecirc;n hệ, th&ocirc;ng tin về tr&igrave;nh độ học vấn cũng như  kinh nghiệm nghề nghiệp của ứng vi&ecirc;n. B&ecirc;n cạnh đ&oacute;, họ cũng muốn biết về  mức lương bạn nhận được trong c&aacute;c c&ocirc;ng việc trước đ&acirc;y. Ngo&agrave;i ra, bạn  cũng cần hỏi để được biết r&otilde; khi n&agrave;o bạn c&oacute; thể bắt tay l&agrave;m việc.<br /> <br /> <br /> <br /> 5. Hiểu r&otilde; thời gian l&agrave;m việc của m&igrave;nh<br /> <br /> Thường th&igrave; nh&agrave; tuyển dụng c&oacute; thể hỏi bạn về thời gian cụ thể bạn c&oacute; thể  l&agrave;m việc, chẳng hạn trong tuần bạn l&agrave;m việc được v&agrave;o những ng&agrave;y n&agrave;o, giờ  n&agrave;o, do đ&oacute;, bạn n&ecirc;n chuẩn bị sẵn phần trả lời cho nội dung n&agrave;y.<br /> <br /> <br /> <br /> 6. Ch&uacute; &yacute; tới c&aacute;c tấm biển cần tuyển nh&acirc;n vi&ecirc;n<br /> <br /> Nếu bạn l&agrave; người quan t&acirc;m tới c&ocirc;ng việc b&aacute;n h&agrave;ng, bạn n&ecirc;n chịu kh&oacute; để  mắt tới những tấm biển cần tuyển nh&acirc;n vi&ecirc;n ở c&aacute;c cửa h&agrave;ng, cửa hiệu  trong thị trấn, th&agrave;nh phố gần nơi m&igrave;nh ở. Tuy nhi&ecirc;n, ngay cả kh&ocirc;ng nh&igrave;n  thấy biển th&ocirc;ng b&aacute;o, nếu quan t&acirc;m, bạn vẫn c&oacute; thể hỏi chủ cửa h&agrave;ng về  nhu cầu tuyển dụng.<br /> <br /> <br /> <br /> 7. Để &yacute; mục rao vặt tr&ecirc;n b&aacute;o<br /> <br /> B&ecirc;n cạnh việc quan t&acirc;m tới c&aacute;c tấm biển th&ocirc;ng b&aacute;o tuyển dụng, bạn cũng  n&ecirc;n thường xuy&ecirc;n để mắt thường xuy&ecirc;n tới c&aacute;c mục rao vặt, tờ rơi quảng  c&aacute;o k&egrave;m theo c&aacute;c b&aacute;o. Cũng kh&ocirc;ng &iacute;t c&aacute;c nh&agrave; tuyển dụng vừa hoặc nhỏ  th&iacute;ch đăng tin tuyển dụng tr&ecirc;n b&aacute;o hơn l&agrave; tr&ecirc;n Internet.<br /> <br /> <br /> <br /> 8. Đăng tuyển trực tiếp<br /> <br /> Việc trực tiếp gặp gỡ v&agrave; trao đổi c&ocirc;ng việc với nh&agrave; tuyển dụng thường l&agrave;  c&aacute;ch tốt nhất để t&igrave;m việc part-time. Vẫn cần nhắc lại với bạn khi đăng  tuyển trực tiếp như thế n&agrave;y, bạn cần ăn vận chỉnh tề, sạch sẽ, m&oacute;ng ch&acirc;n  m&oacute;ng tay cắt tỉa gọn g&agrave;ng, giầy d&eacute;p lịch sự,v.v.<br /> <br /> 9. Đăng tuyển trực tiếp n&ecirc;n mang theo g&igrave;?<br /> <br /> Bạn n&ecirc;n chuẩn bị sẵn những th&ocirc;ng tin cần phải điền v&agrave;o đơn xin việc, kể  cả t&ecirc;n v&agrave; địa chỉ của những nh&agrave; tuyển dụng trước đ&acirc;y của bạn, ng&agrave;y th&aacute;ng  tuyển dụng, danh s&aacute;ch những người c&oacute; thể gi&uacute;p nh&agrave; tuyển dụng mới tham  khảo th&ocirc;ng tin th&ecirc;m về bạn v&agrave; một bản CV nếu bạn c&oacute;. H&atilde;y nhớ l&agrave; lu&ocirc;n sẵn  s&agrave;ng tinh thần cho một cuộc phỏng vấn tại chỗ, hiểu r&otilde; khi n&agrave;o bạn c&oacute;  thể bắt đầu c&ocirc;ng việc v&agrave; số giờ mỗi tuần bạn c&oacute; thể đảm nhiệm.<br /> <br /> <br /> <br /> 10. Sẵn s&agrave;ng danh s&aacute;ch những người tham khảo th&ocirc;ng tin<br /> <br /> Bạn n&ecirc;n c&oacute; trong tay &iacute;t nhất danh s&aacute;ch ba người gồm t&ecirc;n tuổi v&agrave; số điện  thoại, địa chỉ email của họ để sẵn s&agrave;ng đưa ra trong buổi phỏng vấn. Nếu  bạn chưa từng l&agrave;m việc trước đ&oacute;, bạn n&ecirc;n nhờ những người kh&aacute;c như h&agrave;ng  x&oacute;m, bạn b&egrave; th&acirc;n thiết, những người c&oacute; thể hỗ trợ bạn tốt nhất.<br /> <br /> <br /> <br /> 11. Sắp xếp danh s&aacute;ch nh&agrave; tuyển dụng<br /> <br /> Bạn n&ecirc;n c&oacute; một danh s&aacute;ch ghi lại những c&ocirc;ng ty bạn đ&atilde; nộp đơn đăng  tuyển. Sau mỗi cuộc phỏng vấn, bạn n&ecirc;n ghi lại t&ecirc;n người đ&atilde; n&oacute;i chuyện  với bạn v&igrave; c&oacute; thể bạn rất dễ qu&ecirc;n hay nhầm lẫn người nọ với người kia  khi c&ugrave;ng một l&uacute;c đăng tuyển ở nhiều nơi. C&aacute;ch n&agrave;y cũng sẽ gi&uacute;p bạn lu&ocirc;n  sẵn s&agrave;ng khi nhận được điện thoại phản hồi từ nh&agrave; tuyển dụng sau n&agrave;y.<br /> <br /> <br /> <br /> 12. Tận dụng mọi nguồn hỗ trợ th&ocirc;ng tin<br /> <br /> Tất nhi&ecirc;n t&igrave;m việc vẫn l&agrave; c&ocirc;ng việc tự th&acirc;n vận động nhưng bạn cũng  kh&ocirc;ng n&ecirc;n bỏ qua c&aacute;c dịch vụ hỗ trợ miễn ph&iacute; hoặc c&oacute; chi ph&iacute; kh&ocirc;ng lớn  với người t&igrave;m việc như c&aacute;c văn ph&ograve;ng hướng nghiệp cho sinh vi&ecirc;n, c&aacute;c  trung t&acirc;m dịch vụ việc l&agrave;m v&agrave; thư viện c&ocirc;ng cộng.<br /> <br /> <br /> <br /> 13. Khai th&aacute;c th&ocirc;ng tin từ những người xung quanh<br /> <br /> Tr&ecirc;n thực tế, rất nhiều c&ocirc;ng việc c&ograve;n bỏ ngỏ m&agrave; kh&ocirc;ng được đăng tuyển.  H&atilde;y n&oacute;i với tất cả những người quen th&acirc;n của bạn về việc bạn đang muốn  c&oacute; một chỗ l&agrave;m v&agrave; nhờ họ chỉ gi&uacute;p nếu c&oacute; th&ocirc;ng tin. Đ&atilde; c&oacute; người t&igrave;m được  việc l&agrave;m nhờ được người b&aacute;c sỹ trị bệnh m&aacute;ch cho, lại cũng c&oacute; người xin  được v&agrave;o thực tập tại một c&ocirc;ng ty rất tốt v&igrave; đ&atilde; đề cập tới vấn đề n&agrave;y  trong một bữa tiệc sinh nhật,v.v. C&ocirc;ng việc sẽ kh&ocirc;ng bao giờ l&agrave; c&aacute;nh cửa  hẹp với những người lu&ocirc;n cố gắng nỗ lực hết m&igrave;nh trong cuộc sống.</p>','2011-03-02 13:40:46','admin@jobbid.com',3,1),(13,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL),(14,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL),(15,'ky-nang-tim-viec-lam-them','Kỹ năng tìm việc làm thêm','http://localhost/mycms/images/upload/1290442340_Lighthouse.jpg','Nhu cầu về việc làm thêm (việc part-time) là khá lớn trong đời sống. Từ học sinh, sinh viên, những người đã nghỉ hưu hay cả những người đang đi làm đều muốn kiếm thêm thu nhập ngoài giờ hành chính.','<p>Nhu cầu về việc l&agrave;m th&ecirc;m (việc part-time) l&agrave; kh&aacute; lớn trong đời sống.  Từ học sinh, sinh vi&ecirc;n, những người đ&atilde; nghỉ hưu hay cả những người đang  đi l&agrave;m đều muốn kiếm th&ecirc;m thu nhập ngo&agrave;i giờ h&agrave;nh ch&iacute;nh.</p>\n<p>Để t&igrave;m được việc l&agrave;m th&ecirc;m đem lại hiệu quả như mong muốn, c&aacute;c bạn cần chủ động v&agrave; l&agrave;m theo một số lời khuy&ecirc;n sau:</p>\n<p>1. Lu&ocirc;n sẵn s&agrave;ng</p>\n<p>Nếu bạn c&oacute; &yacute; định t&igrave;m việc l&agrave;m th&ecirc;m, h&atilde;y chuẩn bị tinh thần sẵn s&agrave;ng  với những cuộc phỏng vấn nhanh của nh&agrave; tuyển dụng (NTD). H&atilde;y lu&ocirc;n tạo  cho m&igrave;nh nhu cầu t&igrave;m việc l&agrave;m th&ecirc;m, thu thập th&ocirc;ng tin tuyển dụng của  c&aacute;c DN. Nếu bạn đang t&igrave;m kiếm một c&ocirc;ng việc b&aacute;n h&agrave;ng, h&atilde;y để &yacute; tới c&aacute;c  tấm băngr&ocirc;n của c&aacute;c cửa h&agrave;ng, cửa hiệu hoặc ch&uacute; &yacute; tới c&aacute;c mục quảng c&aacute;o  đăng tuyển dụng của c&aacute;c doanh nghiệp tr&ecirc;n c&aacute;c b&aacute;o.</p>\n<p>2. Sử dụng c&ocirc;ng cụ t&igrave;m kiếm online</p>\n<p>C&oacute; kh&aacute; nhiều trang tin tuyển dụng trực tuyến tr&ecirc;n mạng Internet hiện  nay, cung cấp nhiều việc l&agrave;m part-time như vietnamworks.com,  kiemviec.com, tuyendung.com&hellip; Những trang n&agrave;y đều c&oacute; ph&acirc;n loại c&ocirc;ng việc  theo ng&agrave;nh nghề, khu vực, n&ecirc;n rất dễ t&igrave;m việc l&agrave;m th&ecirc;m. Với nhiều trang  web, bạn c&ograve;n c&oacute; thể nộp đơn trực tuyến tới nh&agrave; tuyển dụng.</p>\n<p>3. Hiểu r&otilde; thời gian l&agrave;m việc của m&igrave;nh</p>\n<p>Khi được nh&agrave; tuyển dụng mời phỏng vấn, một c&acirc;u hỏi thường được họ đặt  ra l&agrave; bạn c&oacute; thể l&agrave;m việc cụ thể trong thời gian n&agrave;o, do đ&oacute;, bạn n&ecirc;n  chuẩn bị sẵn phần trả lời cho nội dung n&agrave;y. C&oacute; được sự chuẩn bị kỹ sẽ  gi&uacute;p bạn chủ động trong việc &ldquo;thương lượng&rdquo; với NTD.</p>\n<p>4. Đăng tuyển trực tiếp</p>\n<p>Việc trực tiếp gặp gỡ v&agrave; trao đổi c&ocirc;ng việc với NTD thường l&agrave; c&aacute;ch  tốt nhất để t&igrave;m việc part-time. Với c&aacute;ch n&agrave;y, NTD sẽ hiểu về bạn nhiều  hơn v&agrave; nếu bạn g&acirc;y ấn tượng th&igrave; cơ hội t&igrave;m được việc sẽ cao. Vẫn cần  phải nhắc l&agrave; khi đi gặp NTD, bạn cần ăn mặc lịch sự, giản dị, sạch sẽ,  kh&ocirc;ng qu&aacute; trau chuốt.</p>\n<p>5. Chuẩn bị cho buổi phỏng vấn trực tiếp</p>\n<p>Chuẩn bị cho một cuộc trả lời phỏng vấn, bạn n&ecirc;n chuẩn bị sẵn những  th&ocirc;ng tin cần phải điền v&agrave;o đơn xin việc, kể cả t&ecirc;n v&agrave; địa chỉ của những  NTD trước đ&acirc;y của bạn, danh s&aacute;ch những người c&oacute; thể gi&uacute;p nh&agrave; tuyển dụng  mới tham khảo th&ocirc;ng tin th&ecirc;m về bạn v&agrave; một bản CV nếu bạn c&oacute;. H&atilde;y nhớ  l&agrave; lu&ocirc;n sẵn s&agrave;ng tinh thần cho một cuộc phỏng vấn tại chỗ, hiểu r&otilde; khi  n&agrave;o bạn c&oacute; thể bắt đầu c&ocirc;ng việc v&agrave; số giờ mỗi tuần bạn c&oacute; thể đảm  nhiệm.</p>\n<p>6. L&ecirc;n danh s&aacute;ch người &ldquo;bảo trợ th&ocirc;ng tin&rdquo;</p>\n<p>Bạn n&ecirc;n c&oacute; trong tay &iacute;t nhất danh s&aacute;ch ba người gồm t&ecirc;n tuổi v&agrave; số  điện thoại, địa chỉ email của họ để sẵn s&agrave;ng đưa ra trong buổi phỏng vấn  nếu NTD hỏi đến. Trong trường hợp nếu bạn chưa từng l&agrave;m việc trước đ&oacute;,  bạn c&oacute; thể đưa ra những người th&acirc;n của bạn, hoặc bạn b&egrave; của bạn. Đ&acirc;y l&agrave;  th&ocirc;ng tin kh&ocirc;ng chỉ khi đi t&igrave;m việc part-time m&agrave; khi đi xin việc ch&iacute;nh  thức, nh&agrave; tuyển dụng cũng hay hỏi đến điều n&agrave;y để tham khảo th&ecirc;m th&ocirc;ng  tin.</p>\n<p>7. Sắp xếp danh s&aacute;ch nh&agrave; tuyển dụng</p>\n<p>Bạn n&ecirc;n c&oacute; một danh s&aacute;ch ghi lại những Cty bạn đ&atilde; nộp đơn đăng tuyển.  Sau mỗi cuộc phỏng vấn, bạn n&ecirc;n ghi lại t&ecirc;n người đ&atilde; n&oacute;i chuyện với bạn  v&igrave; c&oacute; thể bạn rất dễ qu&ecirc;n hay nhầm lẫn người nọ với người kia khi c&ugrave;ng  một l&uacute;c đăng tuyển ở nhiều nơi. C&aacute;ch n&agrave;y cũng sẽ gi&uacute;p bạn lu&ocirc;n sẵn s&agrave;ng  khi nhận được điện thoại phản hồi từ nh&agrave; tuyển dụng sau n&agrave;y.</p>\n<p>8. T&igrave;m kiếm nguồn hỗ trợ th&ocirc;ng tin</p>\n<p>Tất nhi&ecirc;n, khi t&igrave;m việc vẫn l&agrave; việc tự th&acirc;n vận động, nhưng bạn cũng  kh&ocirc;ng n&ecirc;n bỏ qua c&aacute;c dịch vụ hỗ trợ miễn ph&iacute; hoặc c&oacute; chi ph&iacute; kh&ocirc;ng lớn  với người t&igrave;m việc như c&aacute;c văn ph&ograve;ng hướng nghiệp cho sinh vi&ecirc;n, c&aacute;c  TTGTVL&hellip;</p>\n<p>9. Khai th&aacute;c th&ocirc;ng tin từ người xung quanh</p>\n<p>Th&ocirc;ng tin c&oacute; thể đến với bạn một c&aacute;ch t&igrave;nh cờ nếu bạn l&agrave; người chia  sẻ suy nghĩ, mong muốn của m&igrave;nh trong việc t&igrave;m việc l&agrave;m th&ecirc;m. Rất c&oacute; thể  trong những buổi n&oacute;i chuyện &ldquo;t&aacute;n gẫu&rdquo; đ&oacute;, những người bạn của bạn sẽ  chỉ cho bạn biết những chỉ ti&ecirc;u tuyển dụng m&agrave; c&aacute;c Cty đang cần.</p>','2011-03-02 15:14:52','admin@jobbid.com',3,1);

/*Table structure for table `comments` */

DROP TABLE IF EXISTS `comments`;

CREATE TABLE `comments` (
  `id` bigint(20) NOT NULL AUTO_INCREMENT,
  `article_id` bigint(20) DEFAULT NULL,
  `ten` varchar(100) DEFAULT NULL,
  `url` varchar(255) DEFAULT NULL,
  `noidung` text,
  `ngaypost` datetime DEFAULT NULL,
  PRIMARY KEY (`id`),
  KEY `FK_comments_articles` (`article_id`),
