<?phpdefined('_JEXEC') or die('Restrict Access');jimport('joomla.application.component.model');class TourdulichModelNhomtour extends JModel {    var $_data;    /**     * Items total     * @var integer     */    var $_total = null;    /**     * Pagination object     * @var object     */    var $_pagination = null;    //pagination page:    function __construct() {        parent::__construct();        global $mainframe, $option;        // Get pagination request variables        $limit = $mainframe->getUserStateFromRequest('global.list.limit', 'limit', $mainframe->getCfg('list_limit'), 'int');        $limitstart = $mainframe->getUserStateFromRequest($option.'.limitstart', 'limitstart', 0, 'int');        // In case limit has been changed, adjust it        $limitstart = ($limit != 0 ? (floor($limitstart / $limit) * $limit) : 0);        $this->setState('limit', $limit);        $this->setState('limitstart', $limitstart);    }    function getData() {        // if data hasn't already been obtained, load it        if (empty($this->_data)) {            $query = $this->_buildQuery();            $this->_data = $this->_getList($query, $this->getState('limitstart'), $this->getState('limit'));        }        return $this->_data;    }    function getTotal() {        // Load the content if it doesn't already exist        if (empty($this->_total)) {            $query = $this->_buildQuery();            $this->_total = $this->_getListCount($query);        }        return $this->_total;    }    function getPagination() {        // Load the content if it doesn't already exist        if (empty($this->_pagination)) {            jimport('joomla.html.pagination');            $this->_pagination = new JPagination($this->getTotal(), $this->getState('limitstart'), $this->getState('limit') );        }        return $this->_pagination;    }    function _buildQuery() {        // Get the WHERE and ORDER BY clauses for the query        $query = "select t1.id,tennhomtour,tendiadiem,diemxuatphat from jos_tdl_nhomtour t1,jos_tdl_dmdiemxuatphat t2 where t1.diemxuatphat=t2.id order by t1.id desc";        return $query;    }    function &getNhomtour($cid)    {        $row =&$this->getTable();                $row->load($cid);        return $row;    }    function store() {        $row =& $this->getTable();        $data = JRequest::get( 'post',JREQUEST_ALLOWRAW );        return $row->save($data);            }    function delete() {        $cids = JRequest::getVar( 'cid', array(0), 'post', 'array' );        $row =& $this->getTable();        if (count( $cids )) {            foreach($cids as $cid) {                if (!$row->delete( $cid )) {                    $this->setError( $row->getErrorMsg() );                    return false;                }            }        }        return true;    }    function getList($iddiemxuatphat)    {        $query="select * from jos_tdl_nhomtour where diemxuatphat=".$iddiemxuatphat;        $db= &JFactory::getDBO();        $db->setQuery($query);        return $db->loadAssocList();    }} ?>