<?phpdefined('_JEXEC') or die('Restrict Access');jimport('joomla.application.component.model');class TourdulichModelTour extends JModel {    var $_data;    /**     * Items total     * @var integer     */    var $_total = null;    /**     * Pagination object     * @var object     */    var $_pagination = null;    //pagination page:    function __construct() {        parent::__construct();        global $mainframe, $option;        // Get pagination request variables        $limit = $mainframe->getUserStateFromRequest('global.list.limit', 'limit', $mainframe->getCfg('list_limit'), 'int');        $limitstart = $mainframe->getUserStateFromRequest($option.'.limitstart', 'limitstart', 0, 'int');        // In case limit has been changed, adjust it        $limitstart = ($limit != 0 ? (floor($limitstart / $limit) * $limit) : 0);        $this->setState('limit', $limit);        $this->setState('limitstart', $limitstart);    }    function getData() {        // if data hasn't already been obtained, load it        if (empty($this->_data)) {            $query = $this->_buildQuery();            $this->_data = $this->_getList($query, $this->getState('limitstart'), $this->getState('limit'));        }        return $this->_data;    }    function getTotal() {        // Load the content if it doesn't already exist        if (empty($this->_total)) {            $query = $this->_buildQuery();            $this->_total = $this->_getListCount($query);        }        return $this->_total;    }    function getPagination() {        // Load the content if it doesn't already exist        if (empty($this->_pagination)) {            jimport('joomla.html.pagination');            $this->_pagination = new JPagination($this->getTotal(), $this->getState('limitstart'), $this->getState('limit') );        }        return $this->_pagination;    }    function _buildQuery() {        // Get the WHERE and ORDER BY clauses for the query        $query = "select t1.id,tentour,giatien,tenloaitour,phuongtien,ngaykhoihanh,hienthi,tennhomtour as nhomtour,t3.tendiadiem diemxuatphat,t4.tendiadiem diemden from jos_tdl_tour t1,jos_tdl_nhomtour t2,jos_tdl_dmdiemxuatphat t3,jos_tdl_dmdiemden t4,jos_tdl_loaitour t5 where t1.nhomtour=t2.id and t2.diemxuatphat=t3.id and t1.diemden=t4.id and t3.loaitour=t5.id order by t1.id desc";       // die($query);        return $query;    }    function &getTour()    {        $row =& $this->getTable();        $cid = JRequest::getVar( 'cid', array(0), '', 'array' );        $row->load($cid[0]);        return $row;    }    function store($data) {        $row =& $this->getTable();        return $row->save($data);            }    function delete() {        $cids = JRequest::getVar( 'cid', array(0), 'post', 'array' );        $row =& $this->getTable();        if (count( $cids )) {            foreach($cids as $cid) {                if (!$row->delete( $cid )) {                    $this->setError( $row->getErrorMsg() );                    return false;                }            }        }        return true;    }} ?>