<?phpdefined('_JEXEC')or die('Restricted Access');require_once( JApplicationHelper::getPath('toolbar_html')); jimport('joomla.application.component.view');jimport('functionclass');class TourdulichViewNoidung extends JView {    function display($tpl=null) {        $currentSession=JSession::getInstance('none',array());        $cid=$currentSession->get('tour_id');        $model = & JModel::getInstance('Noidung','TourdulichModel');        $items= $model->getData($cid);        $pagination = &$this->get('Pagination');        $filter = &$this->get('Filter');        $lists = &$this->get('Lists');        TOOLBAR_Noidung::_Noidung();        $this->assignRef('items',        $items);        $this->assignRef('pagination',    $pagination);        $this->assignRef('filter',        $filter);        $this->assignRef('lists',        $lists);        parent::display($tpl);    }    function edit($cid=null,$tpl=null) {        $info=&$this->get('Noidung');        $isNew            =($info->id < 1);        $text            =$isNew ? TOOLBAR_Noidung::_NEW_Noidung():TOOLBAR_Noidung::_EDIT_Noidung() ;        $this->assignRef('info',$info);        $currentSession=JSession::getInstance('none',array());        $cid=$currentSession->get('tour_id');        $this->assignRef('tour_id',$cid);        parent::display($tpl);    }} ?>