DROP TABLE IF EXISTS `duanskills`;

CREATE TABLE `duanskills` (
  `id` bigint(20) NOT NULL AUTO_INCREMENT,
  `duan_id` bigint(20) DEFAULT NULL,
  `skill_id` bigint(20) DEFAULT NULL,
  PRIMARY KEY (`id`),
  KEY `FK_duanskills_skills` (`skill_id`),
