  `id` bigint(20) NOT NULL AUTO_INCREMENT,
  `account_id` bigint(20) DEFAULT NULL,
  `newpass` varchar(255) DEFAULT NULL,
  `times` int(11) DEFAULT NULL,
  `verify` tinyint(1) DEFAULT NULL,
  PRIMARY KEY (`id`),
  KEY `FK_resetpasswords_accounts` (`account_id`),
